//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
var pjt;
var hno;
var hst;
var ccg;
var exc;
function fn_OnLoadCompleted(obj) {
	gfn_initForm();
	ed_pjt.Text = pjt;
	ed_pjt2.Text = pjt;
	ed_hno.Text = hno;
	ufn_initCombo();

	var codeArr = ["CS116","CS112","CS121","CS123"];
	var dsObjArr = [ds_cyGubun,ds_zgubun,ds_spec4,ds_bld];
	var addTypeArr = [G_CODE_NONE,G_CODE_BLANK,G_CODE_BLANK,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	ds_cond.SetColumn(0,"PJT",pjt);
	ds_cond.SetColumn(0,"HNO",hno);
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_cyGubun.Index = 0;
	cb_zgubun.Index = 0;
	cb_spec4.Index = 0;
	cb_bld.Index = 0;

	fn_query();
}
/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	ds_cond.SetColumn(0,"HNO",ed_hno.Text);

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs17:CS1701002A_S4");
	tit_addSearchActionInfo("cs17:CS1701002A_S5");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_pjt=ds_pjt ds_hno=ds_hno"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	if(fn_saveCheck()==true){
		ds_hno.SetColumn(0, "CCG", ccg);
		ds_hno.SetColumn(0, "EXC", exc);
		ds_hno.SetColumn(0, "BLD", cb_bld.Value);

		tit_clearActionInfo();
		tit_addSingleActionInfo("cs17:CS1701002A_I1");
		tit_addSingleActionInfo("cs17:CS1701002A_I2");

//		if(hst == "Y") {
			tit_addSingleActionInfo("cs17:CS1701002A_I5");
//		} else {
//			tit_addSingleActionInfo("cs17:CS1701002A_I3");
//		}
		tit_addSingleActionInfo("cs17:CS1701002A_I4");
		tit_addSingleActionInfo("cs17:CS1701002A_U1");
		tit_addSingleActionInfo("cs17:CS1701002A_U2");
		tit_callService(
			""
			, ""
			, "ds_hno=ds_hno"
			, ""
			, ""
			, ""
			, true
			, true
			, true
			);
	}
}
function fn_saveCheck(){
    if(length(cb_bld.Value)==0){
		//alert("입주일자를 입력하십시오.");
		gfn_showAlert("CW00001","건물용도");
		return false;
	}
	if(ed_pjt.Text==""){
		//alert("Proj No.를 입력하십시오.");
		gfn_showAlert("CW00001","Proj No.");
		return false;
	}
	if(ed_hno.Text==""){
		//alert("호기번호를 입력하십시오.");
		gfn_showAlert("CW00001","호기번호");
		return false;
	}
	if(length(trim(ed_hno.Value)) < 3){
		gfn_showAlert("호기번호를 올바르게 입력하십시요.");
		return false;
	}
	if(cb_zgubun.Value==""){
		//alert("빌딩구분을 선택하십시오.");
		gfn_showAlert("CW00001","빌딩구분");
		return false;
	}
	if(cb_spec1.Value=="X"){
		//alert("기종을 입력하십시오.");
		gfn_showAlert("CW00001","기종");
		return false;
	}
	if(ed_spec2.Text==""){
		//alert("사양을 입력하십시오.");
		gfn_showAlert("CW00001","사양");
		return false;
	}
	if(cb_bsu.Value==""){
		//alert("보수업체를 선택하십시오.");
		gfn_showAlert("CW00001","보수업체");
		return false;
	}
	if(med_ipjuDate.Text==""){
		//alert("입주일자를 입력하십시오.");
		gfn_showAlert("CW00001","입주일자");
		return false;
	}
	if(ed_bpm.Text==""){
		//alert("보수PM을 입력하십시오.");
		gfn_showAlert("CW00001","보수PM");
		return false;
	}
	if(cb_juj.Value==""){
		//alert("주점검자를 선택하십시오.");
		gfn_showAlert("CW00001","주점검자");
		return false;
	}
	if(cb_buj.Value==""){
		//alert("부점검자를 선택하십시오.");
		gfn_showAlert("CW00001","부점검자");
		return false;
	}
	if(cb_spec4.Value==""){
		//alert("용도를 선택하십시오.");
		gfn_showAlert("CW00001","용도");
		return false;
	}
	if(ed_spec3.Text==""){
		//alert("속도를 입력하십시오.");
		gfn_showAlert("CW00001","속도");
		return false;
	}
	if(ed_spec6.Text==""){
		//alert("용량을 입력하십시오.");
		gfn_showAlert("CW00001","용량");
		return false;
	}
	if(ed_spec7.Text==""){
		//alert("층수을 입력하십시오.");
		gfn_showAlert("CW00001","층수");
		return false;
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs17:CS1701002A_S6");

	tit_callService(
        ""
        , ""
        , "ds_hno=ds_hno"
        , "ds_cnt=ds_cnt"
        , ""
        , ""
        , true
        , true
        , true
        );

	if(ds_cnt.GetColumn(0,"COUNT")!=0){
		//alert("해당 호기가 이미 호기정보에 존재합니다.");
		gfn_showAlert("CW00111");
		return false;
	}
	return true;
}
/********************************************************************************
* 기      능   : Delete
********************************************************************************/
function fn_delete() {
	if(fn_deleteCheck()==true){
		tit_clearActionInfo();
		tit_addSearchActionInfo("cs17:CS1701002A_S7");
		tit_addSearchActionInfo("cs17:CS1701002A_S8");

		tit_callService(
			""
			, ""
			, "ds_hno=ds_hno"
			, "ds_cnt=ds_cnt ds_cnt2=ds_cnt2"
			, ""
			, ""
			, true
			, true
			, true
			);

		tit_clearActionInfo();
		tit_addSingleActionInfo("cs17:CS1701002A_D1");
		tit_addSingleActionInfo("cs17:CS1701002A_D2");
		tit_addSingleActionInfo("cs17:CS1701002A_D3");
		tit_addSingleActionInfo("cs17:CS1701002A_D4");
		if(ds_cnt2.GetColumn(0,"COUNT")==0){
			tit_addSingleActionInfo("cs17:CS1701002A_D5");
			tit_addSingleActionInfo("cs17:CS1701002A_D6");
		}else{
			tit_addSingleActionInfo("cs17:CS1701002A_U3");
		}
		if(ds_cnt.GetColumn(0,"COUNT")==0){

		}else{
			tit_addSingleActionInfo("cs17:CS1701002A_U4");
		}
		tit_callService(
			""
			, ""
			, "ds_hno=ds_hno"
			, ""
			, ""
			, "fn_query"
			, true
			, true
			, true
			);
	}
}
function fn_deleteCheck(){
	if(ed_pjt.Text==""){
		//alert("Proj No.를 입력하십시오.");
		gfn_showAlert("CW00001","Proj No.");
		return false;
	}
	if(ed_hno.Text==""){
		//alert("호기번호를 입력하십시오.");
		gfn_showAlert("CW00001","호기번호");
		return false;
	}
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs17:CS1701002A_S7");

	tit_callService(
        ""
        , ""
        , "ds_hno=ds_hno"
        , "ds_cnt=ds_cnt"
        , ""
        , ""
        , true
        , true
        , true
        );

	if(ds_cnt.GetColumn(0,"COUNT")!=0){
		//alert("해당 호기가 이미 매출계획정보에 존재하므로 삭제가 불가합니다.");
		gfn_showAlert("CW00109","매출계획");
		return false;
	}
	return true;
}
/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close() {
	parent.fn_query();
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리
	gfn_showSearchMsg(grd_list);
	if(ds_pjt.GetRowCount()==0) ds_pjt.AddRow();
	if(ds_hno.GetRowCount()==0) ds_hno.AddRow();
	ed_pjt2.Text = ed_pjt.Text;
	ufn_chgBsu();
}
/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}
}
//=========================================================================================
// [ PART 5 ]
// Detail Process Event 정의 ( User Define function )
// 사용자 정의 함수는 ufn_xxxx() 형태로 작성
//=========================================================================================
/********************************************************************************
* 기      능   : 사용자 정의 함수
********************************************************************************/
function ufn_openPjt() {
	var arg = "pjt="+quote(ed_pjt.Text)+" strId=M";
	gfn_openPopup("CS9601002A",true,arg);
}

function ufn_initCombo(){
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs17:CS1701002A_S1");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_bsu=ds_bsu"
        , ""
        , ""
        , true
        , true
        , true
        );
}
function ufn_chgBsu(){
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs17:CS1701002A_S10");
	tit_addSearchActionInfo("cs17:CS1701002A_S2");
	tit_addSearchActionInfo("cs17:CS1701002A_S3");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_bpm=ds_bpm ds_juj=ds_juj ds_buj=ds_buj"
        , "BSU="+quote(cb_bsu.Value)
        , "ufn_afterChgBsu"
        , true
        , true
        , true
        );
}
function ufn_afterChgBsu(){
	ds_hno.FireEvent=false;
	ds_hno.SetColumn(0,"BPM",ds_bpm.GetColumn(0,"BPM"));
	ds_hno.SetColumn(0,"BPMTEXT",ds_bpm.GetColumn(0,"BPMTEXT"));
	ds_hno.FireEvent=true;
}
function ufn_OnPjtChanged(obj, strText){
	ed_pjt2.Text = ed_pjt.Text;
	ed_hno.Text = "";
}

//=========================================================================================
// [ PART 6 ]
// component Event 정의 : 각 Component 별 이벤트
//=========================================================================================
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리
********************************************************************************/
function ds_hno_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var str = substr(varNewValue,0,1);
	if(strColumnID=="HNO" && varNewValue!=""){
		if(!(str=="L" || str=="S" || str=="W" || str=="G" || str=="M" || str=="J")){
			//alert("호기번호는 'L,S,W,G,M,J' 로 시작되어야합니다.");
			gfn_showAlert("CW00112");
			ds_hno.FireEvent=false;
			ds_hno.SetColumn(0,"HNO","");
			ds_hno.FireEvent=true;
		}
	}
}
