function fn_OnLoadCompleted(obj) 
{
	gfn_initForm(true);

	var codeArr = ["CS116","CS115","CS115"];	
	var dsObjArr = [ds_cyGubun,ds_ara,ds_area_cd];
	var addTypeArr = [G_CODE_SELECT,G_CODE_SELECT,G_CODE_ALL];

	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);	

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) 
{
	var user_grp = gfn_getUserGroup();
	btn_save.Enable = false;
	if(user_grp == "20" || user_grp == "30") { // 보수PM일 경우
//		ds_cond.SetColumn(0, "ARA", G_AREA_CODE);
		ds_cond.SetColumn(0, "ARA", '');
		cb_area.Enable = false;
		chk_confirm.Enable = false;
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "ARA", '');
		cb_area.Index = 0;
	} else {
		chk_confirm.enable = false;
	}
	if(G_USER_ID == "2021200" || G_USER_ID == "2023687" || G_USER_ID == "2018900" || G_USER_ID == "2029758" || G_USER_ID == "2999999") {	
		btn_save.Enable = true;
	}

	cb_cyGubun.Index = 0;
	cb_ara.Index = 0;

	chk_confirm.Value = 0;	
	cal_yymm1.Value = substr(gfn_getCurrDate(),0,4) + "0101";
	cal_yymm2.Value = gfn_getCurrDate();

	// 임시 승인버튼 추가 == 향후에는 사용안함
	if( (G_USER_ID == "2019523" || G_USER_ID == "2017288" || G_USER_ID == "2018900") && (gfn_getCurrDate() <= '20190322') ) 
	{    //윤아로,    한정훈,    방기태		
			btn_tmpapproval.Visible = true;
	}

}
