* 기      능   : 조회 1
********************************************************************************/
function fn_query() 
{	/* 미승인 호기가 있는 pjT만 조회하는 경우 */
	if(chk_confirm.Value == "1"){ 
		ds_cond.SetColumn(0, "CHK", "Y");
	}else{
		ds_cond.SetColumn(0, "CHK", "");
	}

    tit_clearActionInfo();
	tit_addSearchActionInfo("cs17:CS1702001A_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"
		, ""
		, "fn_afterQuery"
		, true
		, true
		, true
		);	
}

/********************************************************************************
* 기      능   : 프로젝트 생성 
********************************************************************************/
function fn_createPjt()
{	
	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return false;
	}
	if(ds_list.rowcount() > 1){
		gfn_showMsg("CW00234");
		return false;
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs17:CS1702001A_S2");
	tit_callService(
        ""
        , ""
        , "ds_list=ds_list"
        , "ds_cnt=ds_cnt"
        , ""
        , ""
        , true
        , true
        , true
        );

	if(ds_cnt.GetColumn(0,"COUNT")!=0){
		gfn_showMsg("CW00234");
		return false;
	} 	 
	ufn_getPjt();
}

function fn_savePjt(){

	ds_list2.ClearData();

	if(fn_saveCheck(0) == true){

		ds_list2.AddRow();
		//ds_list2.SetColumn(0, "NAM", ds_list.GetColumn(0, "NAM"));	
		ds_list2.SetColumn(0, "NAM", REPLACE(REPLACE(ds_list.GetColumn(0, "NAM"), CHR(10), ''), CHR(13), '')  );

		//ds_list2.SetColumn(0, "SPT", ds_list.GetColumn(0, "SPT"));
		ds_list2.SetColumn(0, "SPT", REPLACE(REPLACE(ds_list.GetColumn(0, "SPT"), CHR(10), ''), CHR(13), '')  );

		ds_list2.SetColumn(0, "ADDR1", ds_list.GetColumn(0, "ADDR1"));
		ds_list2.SetColumn(0, "ADDR2", ds_list.GetColumn(0, "ADDR2"));
		ds_list2.SetColumn(0, "TEL", ds_list.GetColumn(0, "TEL"));
		ds_list2.SetColumn(0, "INSUDATE", ds_list.GetColumn(0, "INSUDATE"));
		ds_list2.SetColumn(0, "ARA", ds_list.GetColumn(0, "ARA"));
		ds_list2.SetColumn(0, "CYGUBUN", ds_list.GetColumn(0, "CYGUBUN"));
	}else {
		return false;
	}	

	tit_clearActionInfo();	
	tit_addSingleActionInfo("cs17:CS1702001A_I1");	
	tit_addSingleActionInfo("cs17:CS1702001A_I2");	
	tit_callService(
	""
	, ""
	, "ds_list2=ds_list2"
	, ""
	, "MJT="+quote(ds_pjt.GetColumn(0,"MJT"))
	, "fn_afterSavePjt"
	, true
	, true
	, true
	);   
}
/********************************************************************************
* 기      능   : 프로젝트 수정 
********************************************************************************/
function fn_save()
{	
	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return false;
	}	

	if(ds_list.GetRowCount() < 2 && ds_list.GetColumn(0, "PJT") == null){
		gfn_showMsg("CW00053", "수정");
		return false;
	}
	var j = 0;

	ds_list2.ClearData();

	for(var i=0; i<ds_list.RowCount(); i++) {

		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우

			if(fn_saveCheck(i) == true){

				ds_list2.AddRow();
				ds_list2.SetColumn(j, "PJT", ds_list.GetColumn(i, "PJT"));				
				//ds_list2.SetColumn(j, "NAM", ds_list.GetColumn(i, "NAM"));
				ds_list2.SetColumn(j, "NAM", REPLACE(REPLACE(ds_list.GetColumn(i, "NAM"), CHR(10), ''), CHR(13), '')  );

				//ds_list2.SetColumn(j, "SPT", ds_list.GetColumn(i, "SPT"));
				ds_list2.SetColumn(j, "SPT", REPLACE(REPLACE(ds_list.GetColumn(i, "SPT"), CHR(10), ''), CHR(13), '')  );

				ds_list2.SetColumn(j, "ADDR1", ds_list.GetColumn(i, "ADDR1"));
				ds_list2.SetColumn(j, "ADDR2", ds_list.GetColumn(i, "ADDR2"));
				ds_list2.SetColumn(j, "TEL", ds_list.GetColumn(i, "TEL"));
				ds_list2.SetColumn(j, "INSUDATE", ds_list.GetColumn(i, "INSUDATE"));
				ds_list2.SetColumn(j, "ARA", ds_list.GetColumn(i, "ARA"));
				ds_list2.SetColumn(j, "CYGUBUN", ds_list.GetColumn(i, "CYGUBUN"));

				j++;
			} else {
				return false;
			}
		}
	}

	tit_clearActionInfo();

	if(ds_list2.GetRowCount() > 0) {
		tit_addMultiActionInfo("cs17:CS1702001A_U1"); // zmaster01
		tit_addMultiActionInfo("cs17:CS1702001A_U2"); // zmaster02
		tit_addMultiActionInfo("cs17:CS1702001A_U3"); // zcst101
		tit_addMultiActionInfo("cs17:CS1702001A_U4"); // zcst111
		tit_callService(
			""
			, ""
			, "ds_list2=ds_list2"
			, ""
			, ""
			, "fn_afterSave"
			, true);
	}
}



/********************************************************************************
* 기      능   : 저장 검사
********************************************************************************/
function fn_saveCheck(i){	

	var seq = i+1+ "번째 정보의 ";

	if(length(trim(ds_list.GetColumn(i, "NAM"))) == 0){
		var arr = [seq + trim(st_nam.Text)];
		gfn_showMsg("CW00001", arr);
		return false;
	}
	if(length(trim(ds_list.GetColumn(i, "SPT"))) == 0){		
		var arr = [seq + trim(st_spt.Text)];
		gfn_showMsg("CW00001", arr);
		return false;
	} 
	if(length(trim(ds_list.GetColumn(i, "ADDR1"))) == 0){		
		var arr = [seq + trim(st_addr.Text)];
		gfn_showMsg("CW00001", arr);
		return false;
	} 
	if(length(trim(ds_list.GetColumn(i, "TEL"))) == 0){		
		var arr = [seq + trim(st_tel.Text)];
		gfn_showMsg("CW00001", arr);
		return false;
	} 
	if(length(trim(ds_list.GetColumn(i, "INSUDATE"))) == 0){		
		var arr = [seq + trim(st_insuDate.Text)];
		gfn_showMsg("CW00001", arr);
		return false;
	} 
	if(length(trim(ds_list.GetColumn(i, "ARA"))) == 0){		
		var arr = [seq + trim(st_ara.Text)];
		gfn_showMsg("CW00001", arr);
		return false;
	}
	if(length(trim(ds_list.GetColumn(i, "CYGUBUN"))) == 0) {		
		var arr = [seq + trim(st_cyGubun.Text)];
		gfn_showMsg("CW00001", arr);
		return false;
	} 
	return true;
}
