/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}

	fn_query();
}
function fn_afterSavePjt(errCode, errMsg) 
{
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}
	ds_cond.SetColumn(0, "PJT", ds_pjt.GetColumn(0, "MJT"));
	fn_query();
}
