/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ){
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	}else{
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - 호기등록 버튼 처리 
********************************************************************************/
function ds_cond_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
}

function ed_pjt_OnChanged(obj,strText)
{
	if(length(ed_pjt.value) > 0) {
		if(substr(ed_pjt.value,0,1) <> "M") {
			ed_nam.Enable = false;
			ed_spt.Enable = false;
		} else {
			ed_nam.Enable = true;
			ed_spt.Enable = true;
		}
	}
}

function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 10) {			
		var arg = "pjt=" + quote(ds_list.GetColumn(nRow,"PJT")) + " ara=" + quote(ds_list.GetColumn(nRow,"ARA"));
		gfn_openPopup("CS1702002A", true, arg);	
	}
}

function chk_confirm_OnClick(obj,strValue)
{
	if(chk_confirm.Value == "1"){
		cal_yymm1.Enable = false;
		cal_yymm2.Enable = false;
		cb_area.Enable = false;
		btn_pjt.Enable = false;	 	     
	}else{
		cal_yymm1.Enable = true;
		cal_yymm2.Enable = true;
		cb_area.Enable = true;
		btn_pjt.Enable = true;		
	}
}

function btn_trash_OnClick(obj)
{
	ds_cond.setColumn(0, "PJT", "");
	ed_pjt.value = "";
}

