//========================================시작======================================
function fn_tmpapproval()
{	
	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return false;
	}	

	if(ds_list.GetRowCount() < 2 && ds_list.GetColumn(0, "PJT") == null){
		gfn_showMsg("CW00053", "수정");
		return false;
	}
	var j = 0;



	for(var i=0; i<ds_list.RowCount(); i++) {

		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우		
			//ds_list3.ClearData();
			//ds_list3.AddRow();	
			gfn_initDs(ds_list3, true );
			ds_list3.SetColumn(0, "PJT", ds_list.GetColumn(i, "PJT"));		
//trace(ds_list3.SaveXML());							
			tit_clearActionInfo();
			tit_addSearchActionInfo("cs17:CS1702002A_S5");
			tit_callService(
				""
				, ""
				, "ds_cond=ds_list3"
				, "ds_list_tmp=ds_list"
				, ""
				, "fn_afterQuerytmp"
				, true
				, true
				, true
				);						

		}
	}

	fn_query() ;
}

