#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var user_grp = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	user_grp = gfn_getUserGroup();

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,0,10);
	cb_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_montht.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_yearf.SetFocus();

	//지역,보수PM SELECT
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs32:CS3201001A_S1");
	tit_addSearchActionInfo("cs32:CS3201001A_S2");

	tit_addSearchActionInfo("cs32:CS3201001A_S8"); // 팀 추가
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_ara=ds_ara ds_bpm=ds_bpm ds_tem=ds_tem"
		, ""
		, "fn_afterQueryCd"
		, true,true);
}

/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_yearf_cd.AddRow();
		ds_yearf_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yearf_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		ds_yeart_cd.AddRow();
		ds_yeart_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yeart_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_yearf.index = eNum;
	cb_yeart.index = eNum;
}
/********************************************************************************
* 기      능   : 조회 Callback  :주점검자,부점검자 콤보
********************************************************************************/
function fn_afterQueryCd(errCode, errMsg) {

}
/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	//년도필드 필수체크	
	if(gfn_isNull(cb_yearf, true, st_yymm.value))
		return;
	//월필드 필수체크	
	if(gfn_isNull(cb_monthf, true, st_yymm.value))
		return;
	if(gfn_isNull(cb_yeart, true, st_yymm.value))
		return;
	if(gfn_isNull(cb_montht, true, st_yymm.value))
		return;
	if(parseInt(ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF")) < 
	   parseInt(substr(gfn_getCurrDate(),0,6)))
	{
		gfn_showMsg("CW00123",st_yymm.Value);
		cb_monthf.SetFocus();
		return;
	}   
	if(parseInt(ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT")) < 
	   parseInt(substr(gfn_getCurrDate(),0,6)))
	{
		gfn_showMsg("CW00123",st_yymm.Value);
		cb_montht.SetFocus();
		return;
	}   
	if(parseInt(ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF")) > 
	   parseInt(ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT")))
	{
		gfn_showMsg("CW00009");
		cb_yeart.SetFocus();
		return;
	}   

	var fYear  = parseInt(ds_cond.GetColumn(0,"YEARF"));
	var tYear  = parseInt(ds_cond.GetColumn(0,"YEART"));
	var fMonth = parseInt(ds_cond.GetColumn(0,"MONTHF"));
	var tMonth = parseInt(ds_cond.GetColumn(0,"MONTHT"));

	//조회기간: 0=같은달,1=2개월,2=3개월..
	if(((tYear-fYear)*12+tMonth-fMonth) > 11)
	{
		gfn_showMsg("CW00078"," 12 ");
		cb_montht.SetFocus();
		return;
	}

	if(parseInt(ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF")) == 
	   parseInt(substr(gfn_getCurrDate(),0,6)) &&
	   parseInt(substr(gfn_getCurrDate(),6,2)) > 10)
	{
		if(ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF") == ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT")){
			ds_cond.SetColumn(0,"YYYYMMF",gfn_getAfterMonth((ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF")),1));
			ds_cond.SetColumn(0,"YYYYMMT",gfn_getAfterMonth((ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT")),1));
		} else {
			ds_cond.SetColumn(0,"YYYYMMF",gfn_getAfterMonth((ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF")),1));
			ds_cond.SetColumn(0,"YYYYMMT",ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT"));
		}		
	}
	else
	{
		ds_cond.SetColumn(0,"YYYYMMF",ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF"));
		ds_cond.SetColumn(0,"YYYYMMT",ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT"));
	}

	if(user_grp == "40" && div_code.cb_big.value == "") {
		gfn_showMsg("CW00002");
		div_code.cb_big.setFocus();
		return;
	}

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	tit_clearActionInfo();

	var rowCnt = 0;
	if(div_code.cb_big.value == '1' && div_code.cb_mid.value == '' && div_code.cb_small.value == '') {
		ds_cond.SetColumn(0,"GBN","A");
		tit_addSearchActionInfo("cs32:CS3201001A_S3");
		rowCnt = parseInt(ds_ara.rowcount);
	} else if(div_code.cb_big.value == '2' && div_code.cb_mid.value == '' && div_code.cb_small.value == '') {
		ds_cond.SetColumn(0,"GBN","B");
		tit_addSearchActionInfo("cs32:CS3201001A_S3");
		rowCnt = parseInt(ds_bpm.rowcount);
	} else if(div_code.cb_big.value == '3' && div_code.cb_mid.value == '' && div_code.cb_small.value == '') {
		ds_cond.SetColumn(0,"GBN","E");
		tit_addSearchActionInfo("cs32:CS3201001A_S3");
		rowCnt = parseInt(ds_tem.rowcount);
	} else {
	/*	if(div_code.cb_big.value == '1' || (div_code.cb_big.value == '' && div_code.cb_mid.value != '')
		   || (div_code.cb_big.value == '' && div_code.cb_mid.value == '' && div_code.cb_small.value != '')) {
			ds_cond.SetColumn(0,"GBN","C");
			tit_addSearchActionInfo("cs32:CS3201001A_S4");
		} else if(div_code.cb_big.value == '2' ) {
			ds_cond.SetColumn(0,"GBN","D");
			tit_addSearchActionInfo("cs32:CS3201001A_S5");
		}	
		*/
		ds_cond.SetColumn(0,"GBN","C");
		tit_addSearchActionInfo("cs32:CS3201001A_S9");
		rowCnt = parseInt((parseInt(left(ds_cond.GetColumn(0,"YYYYMMT"),4))-parseInt(left(ds_cond.GetColumn(0,"YYYYMMF"),4)))*12
		         +(parseInt(right(ds_cond.GetColumn(0,"YYYYMMT"),2))-parseInt(right(ds_cond.GetColumn(0,"YYYYMMF"),2)))+1);
	}	

	var args = "";
	var q1="";	
	var q2="";	
	var q3="";	
	var q4="";	
    ds_list.fireevent = false;
	grd_list.Redraw = false;

	for(var i=1; i<=rowCnt; i++){

		q1 += "\n	  ,SUM(DOUBLE(COL"+i+"_CNT)) COL"+i+"_CNT"
			+ "\n	  ,SUM(DOUBLE(COL"+i+"_AMT)) COL"+i+"_AMT";

		if(i == 1) {   
			q2 += "\n	  ,(SUM(DOUBLE(COL"+i+"_CNT))";
		}else{	
			q2 += "+SUM(DOUBLE(COL"+i+"_CNT))";
		}	
		if(i == 1) {   
			q3 += "\n	  ,(SUM(DOUBLE(COL"+i+"_AMT))";
		}else{	
			q3 += "+SUM(DOUBLE(COL"+i+"_AMT))";
		}	

		if(ds_cond.GetColumn(0,"GBN") == "A") {
			q4 += "\n	  	 ,CASE WHEN A.COL1 = '"+ds_ara.getColumn(i-1,"CODE")+"' THEN A.CNT ELSE 0 END COL"+i+"_CNT"
				+ "\n	  	 ,CASE WHEN A.COL1 = '"+ds_ara.getColumn(i-1,"CODE")+"' THEN A.AMT ELSE 0 END COL"+i+"_AMT";
		} else if(ds_cond.GetColumn(0,"GBN") == "B") {
			q4 += "\n	  	 ,CASE WHEN A.COL1 = '"+ds_bpm.getColumn(i-1,"CODE")+"' THEN A.CNT ELSE 0 END COL"+i+"_CNT"
				+ "\n	  	 ,CASE WHEN A.COL1 = '"+ds_bpm.getColumn(i-1,"CODE")+"' THEN A.AMT ELSE 0 END COL"+i+"_AMT";
		} else if(ds_cond.GetColumn(0,"GBN") == "E") {
			q4 += "\n	  	 ,CASE WHEN A.COL1 = '"+ds_tem.getColumn(i-1,"CODE")+"' THEN A.CNT ELSE 0 END COL"+i+"_CNT"
				+ "\n	  	 ,CASE WHEN A.COL1 = '"+ds_tem.getColumn(i-1,"CODE")+"' THEN A.AMT ELSE 0 END COL"+i+"_AMT";
		} else {
			q4 += "\n	  	 ,CASE WHEN A.COL1 = '"+gfn_getAfterMonth(ds_cond.GetColumn(0,"YYYYMMF"),(i-1))+"' THEN A.CNT ELSE 0 END COL"+i+"_CNT"
				+ "\n	  	 ,CASE WHEN A.COL1 = '"+gfn_getAfterMonth(ds_cond.GetColumn(0,"YYYYMMF"),(i-1))+"' THEN A.AMT ELSE 0 END COL"+i+"_AMT";
		}	
	}
	q2 += ") SUM_CNT";
	q3 += ") SUM_AMT";
	//지역별 조회일 경우 지방계 추가
	if(ds_cond.GetColumn(0,"GBN") == "A") {
		q3 += "\n	  	 ,SUM(DOUBLE(COL99_CNT)) COL99_CNT"
			+ "\n	  	 ,SUM(DOUBLE(COL99_AMT)) COL99_AMT";

		q4 += "\n	  	 ,CASE WHEN A.COL1 <> 'E5' THEN A.CNT ELSE 0 END COL99_CNT"
			+ "\n	  	 ,CASE WHEN A.COL1 <> 'E5' THEN A.AMT ELSE 0 END COL99_AMT";
	} 

	args += " S1_QUERY="+quote(q1);
	args += " S2_QUERY="+quote(q2);
	args += " S3_QUERY="+quote(q3);
	args += " S4_QUERY="+quote(q4);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , args
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	ds_list2.ClearData();

	var rowCnt = 0;
	if(ds_cond.GetColumn(0,"GBN") == "A") {
		rowCnt = parseInt(ds_ara.rowcount);
	} else if(ds_cond.GetColumn(0,"GBN") == "B") {
		rowCnt = parseInt(ds_bpm.rowcount);
	} else if(ds_cond.GetColumn(0,"GBN") == "E") {
		rowCnt = parseInt(ds_tem.rowcount);
	} else {
		rowCnt = (parseInt(left(ds_cond.GetColumn(0,"YYYYMMT"),4))-parseInt(left(ds_cond.GetColumn(0,"YYYYMMF"),4)))*12
		         +(parseInt(right(ds_cond.GetColumn(0,"YYYYMMT"),2))-parseInt(right(ds_cond.GetColumn(0,"YYYYMMF"),2)))+1;
	}	

	var content = "";
	content += '<contents>                                                                                                                             \n';
	content += '	<format id="Default">                                                                                                              \n';
	content += '		<columns>                                                                                                                      \n';
	content += '			<col fix="left" width="40"/>                                                                                                          \n';

	//if(ds_cond.GetColumn(0,"GBN") == "C" || ds_cond.GetColumn(0,"GBN") == "D" ) {
	if(ds_cond.GetColumn(0,"GBN") == "C" ) {
		content += '			<col fix="left" width="200"/>                                                                                                         \n';
	} else {
		content += '			<col fix="left" width="80"/>                                                                                                         \n';
	}	

	for(var i=0; i<rowCnt+1; i++){
		content += '		<col width="50"/>                                                                                                          \n';
		content += '		<col width="100"/>                                                                                                         \n';
	}
	if(ds_cond.GetColumn(0,"GBN") == "A") {
		content += '		<col width="50"/>                                                                                                          \n';
		content += '		<col width="100"/>                                                                                                         \n';
	}
	content += '			<col width="0"/>                                                                                                          \n';

	content += '		</columns>                                                                                                                     \n';
	content += '		<head>                                                                                                                         \n';
	content += '			<cell align="center" col="0" color="user21" display="text" font="굴림,9" rowspan="2" text="No"/>                           \n';

//	if(ds_cond.GetColumn(0,"GBN") == "C" || ds_cond.GetColumn(0,"GBN") == "D" ) {
	if(ds_cond.GetColumn(0,"GBN") == "C" ) {
		content += '			<cell align="center" col="1" color="user21" display="text" font="굴림,9" rowspan="2" text="협력사"/>                       \n';
	} else {
		content += '			<cell align="center" col="1" color="user21" display="text" font="굴림,9" rowspan="2" text="년월"/>                       \n';
	}	

	var col = 2;
	for(var i=0; i<rowCnt; i++){
		if(ds_cond.GetColumn(0,"GBN") == "A") {
			content += '		<cell align="center" col="'+col+'" color="user21" colspan="2" display="text" ExpandChar="'+ds_ara.getColumn(i,"CODE")+'" font="굴림,9" text="'+ds_ara.getColumn(i,"CODE_NAME")+'"/>    \n';
		} else if(ds_cond.GetColumn(0,"GBN") == "B") {
			content += '		<cell align="center" col="'+col+'" color="user21" colspan="2" display="text" ExpandChar="'+ds_bpm.getColumn(i,"CODE")+'" font="굴림,9" text="'+ds_bpm.getColumn(i,"CODE_NAME")+'"/>    \n';
		} else if(ds_cond.GetColumn(0,"GBN") == "E") {
			content += '		<cell align="center" col="'+col+'" color="user21" colspan="2" display="text" ExpandChar="'+ds_tem.getColumn(i,"CODE")+'" font="굴림,9" text="'+ds_tem.getColumn(i,"CODE_NAME")+'"/>    \n';
		} else {
			content += '		<cell align="center" col="'+col+'" color="user21" colspan="2" display="text" font="굴림,9" text="'+gfn_getAfterMonth(ds_cond.GetColumn(0,"YYYYMMF"),i)+'"/>    \n';
		}	
		col += 2;
	}
	if(ds_cond.GetColumn(0,"GBN") == "A") {
		content += '		    <cell align="center" col="'+col+'" color="user21" colspan="2" display="text" font="굴림,9" text="지방계"/>    \n';
		col += 2;
	}
	content += '		    <cell align="center" col="'+col+'" color="user21" colspan="2" display="text" font="굴림,9" text="합계"/>    \n';

	col = 2;
	for(var i=0; i<rowCnt; i++){
		content += '			<cell align="center" bkcolor="user23" col="'+col+'" color="user24" display="text" font="굴림,9" row="1" text="대수"/>            \n';
		col++;
		content += '			<cell align="center" bkcolor="user23" col="'+col+'" color="user24" display="text" font="굴림,9" row="1" text="수주잔고금액"/>    \n';
		col++;
	}
	if(ds_cond.GetColumn(0,"GBN") == "A") {
		content += '			<cell align="center" bkcolor="user23" col="'+col+'" color="user24" display="text" font="굴림,9" row="1" text="대수"/>            \n';
		col++;
		content += '			<cell align="center" bkcolor="user23" col="'+col+'" color="user24" display="text" font="굴림,9" row="1" text="수주잔고금액"/>    \n';
		col++;
	}
	content += '			<cell align="center" bkcolor="user23" col="'+col+'" color="user24" display="text" font="굴림,9" row="1" text="대수"/>            \n';
	col++;
	content += '			<cell align="center" bkcolor="user23" col="'+col+'" color="user24" display="text" font="굴림,9" row="1" text="수주잔고금액"/>    \n';
	col++;
	content += '			<cell  col="'+col+'" row="1" />    \n';
	content += '		</head>                                                                                                                        \n';
	content += '		<body>                                                                                                                         \n';
	content += '			<cell align="center" bkcolor2="user22" col="0" display="text" expr="currow+1"/>                                                            \n';

//	if(ds_cond.GetColumn(0,"GBN") == "C" || ds_cond.GetColumn(0,"GBN") == "D") {
	if(ds_cond.GetColumn(0,"GBN") == "C" ) {
		content += '			<cell bkcolor2="user22" col="1" colid="COL2" display="text"/>                                                                           \n';
	} else {
		content += '			<cell align="center" bkcolor2="user22" col="1" colid="COL2" display="text" Mask="####.##"/>                                                                           \n';
	} 	

	col = 2;
	col2 = 1;
	for(var i=0; i<rowCnt; i++){
		content += '		<cell align="right" bkcolor2="user22" col="'+col+'" colid="COL'+col2+'_CNT" display="number"/>                               \n';
		col++;
		content += '		<cell align="right" bkcolor2="user22" col="'+col+'" colid="COL'+col2+'_AMT" display="number"/>                               \n';
		col++;
		col2++;
	}
	if(ds_cond.GetColumn(0,"GBN") == "A") {
		content += '		    <cell align="right" bkcolor2="user22" col="'+col+'" colid="COL99_CNT" display="number"/>                                       \n';
			col++;
		content += '		    <cell align="right" bkcolor2="user22" col="'+col+'" colid="COL99_AMT" display="number"/>                                       \n';
			col++;
	}
	content += '		    <cell align="right" bkcolor2="user22" col="'+col+'" colid="SUM_CNT" display="number"/>                                       \n';
		col++;
	content += '		    <cell align="right" bkcolor2="user22" col="'+col+'" colid="SUM_AMT" display="number"/>                                       \n';
		col++;
	content += '		    <cell col="'+col+'" colid="COL1" />                                       \n';
	content += '		</body>                                                                                                                        \n';

	content += '		<summary>                                                                                                                      \n';
	content += '			<cell align="center" bkcolor="user25" col="0" color="user26" colspan="2" display="text" text="합계"/>                      \n';

	col = 2;
	col2 = 1;
	for(var i=0; i<rowCnt; i++){
		content += '		<cell align="right" bkcolor="user25" col="'+col+'" color="user26" display="number" expr="sum(&quot;COL'+col2+'_CNT&quot;)"/>     \n';
		col++;
		content += '		<cell align="right" bkcolor="user25" col="'+col+'" color="user26" display="number" expr="sum(&quot;COL'+col2+'_AMT&quot;)"/>     \n';
		col++;
		col2++;
	}
	if(ds_cond.GetColumn(0,"GBN") == "A") {
		content += '		<cell align="right" bkcolor="user25" col="'+col+'" color="user26" display="number" expr="sum(&quot;COL99_CNT&quot;)"/>             \n';
			col++;
		content += '		<cell align="right" bkcolor="user25" col="'+col+'" color="user26" display="number" expr="sum(&quot;COL99_AMT&quot;)"/>             \n';
			col++;
	}
	content += '		<cell align="right" bkcolor="user25" col="'+col+'" color="user26" display="number" expr="sum(&quot;SUM_CNT&quot;)"/>             \n';
		col++;
	content += '		<cell align="right" bkcolor="user25" col="'+col+'" color="user26" display="number" expr="sum(&quot;SUM_AMT&quot;)"/>             \n';
		col++;
	content += '		<cell col="'+col+'" />             \n';
	content += '		</summary>                                                                                                                     \n';

	content += '	</format>                                                                                                                          \n';
	content += '</contents>																															   \n';

	grd_list.Contents = content;
	ds_list.fireevent = true;
	grd_list.Redraw = true;

	ds_cond_copy.copy(ds_cond);
}

