	gfn_saveToExcel(grd_list, true );
}
/********************************************************************************
* 기      능   : 그리드 DOUBLE CLICK시 상세조회
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var cCell = (nCell/2)+1;
	if(nCell > 1 && grd_list.GetCellText('Head',-1,cCell) != '합계' && grd_list.GetCellText('Head',-1,cCell) != '지방계')
	{
		var col1 = "";
		var col2 = "";
		ds_cond_copy.SetColumn(0,"YYYYMMF","");
		ds_cond_copy.SetColumn(0,"BSU","");
		ds_cond_copy.SetColumn(0,"ARA","");
		ds_cond_copy.SetColumn(0,"BPM","");
		ds_cond_copy.SetColumn(0,"TEM","");
		//if(ds_cond_copy.GetColumn(0,"GBN") == "C" || ds_cond_copy.GetColumn(0,"GBN") == "D") {
		if(ds_cond.GetColumn(0,"GBN") == "C" ) {
			ds_cond_copy.SetColumn(0,"YYYYMMF",grd_list.GetCellText('Head',-1,cCell));
			ds_cond_copy.SetColumn(0,"BSU",ds_list.GetColumn(nRow,"COL1"));
		} else if(ds_cond_copy.GetColumn(0,"GBN") == "A"){
			ds_cond_copy.SetColumn(0,"YYYYMMF",ds_list.GetColumn(nRow,"COL2"));
			ds_cond_copy.SetColumn(0,"ARA",grd_list.GetCellProp('Head',cCell,"ExpandChar"));
		} else if(ds_cond_copy.GetColumn(0,"GBN") == "B"){
			ds_cond_copy.SetColumn(0,"YYYYMMF",ds_list.GetColumn(nRow,"COL2"));
			ds_cond_copy.SetColumn(0,"BPM",grd_list.GetCellProp('Head',cCell,"ExpandChar"));
		} else if(ds_cond_copy.GetColumn(0,"GBN") == "E"){
			ds_cond_copy.SetColumn(0,"YYYYMMF",ds_list.GetColumn(nRow,"COL2"));
			ds_cond_copy.SetColumn(0,"TEM",grd_list.GetCellProp('Head',cCell,"ExpandChar"));
		} 	

		fn_query2();
	}
}
/********************************************************************************
* 기      능   : grid double click 시 조회 
********************************************************************************/
function fn_query2() {
	tit_clearActionInfo();
	if(ds_cond_copy.GetColumn(0,"GBN") == "B" || ds_cond_copy.GetColumn(0,"GBN") == "E") { // BPM & TEM
		tit_addSearchActionInfo("cs32:CS3201001A_S6", null, true);
	} else {
		tit_addSearchActionInfo("cs32:CS3201001A_S7", null, true);
	}

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond_copy"
        , "ds_list2=ds_list"
        , ""
        , "fn_afterQuery2"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list2.RowCount()+"");
}
