#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var user_grp = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	user_grp = gfn_getUserGroup();

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-10,10);
	cb_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_montht.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_yearf.SetFocus();

	var codeArr = ["CS115"];
	var dsObjArr = [ds_ara];
	var addTypeArr = [G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

}

/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_yearf_cd.AddRow();
		ds_yearf_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yearf_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		ds_yeart_cd.AddRow();
		ds_yeart_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yeart_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_yearf.index = eNum;
	cb_yeart.index = eNum;
}
/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	//년도필드 필수체크	
	if(gfn_isNull(cb_yearf, true, st_yymm.value))
		return;
	//월필드 필수체크	
	if(gfn_isNull(cb_monthf, true, st_yymm.value))
		return;
	if(gfn_isNull(cb_yeart, true, st_yymm.value))
		return;
	if(gfn_isNull(cb_montht, true, st_yymm.value))
		return;
	if(parseInt(ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF")) > 
	   parseInt(ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT")))
	{
		gfn_showMsg("CW00009");
		cb_yeart.SetFocus();
		return;
	}   

	var fYear  = parseInt(ds_cond.GetColumn(0,"YEARF"));
	var tYear  = parseInt(ds_cond.GetColumn(0,"YEART"));
	var fMonth = parseInt(ds_cond.GetColumn(0,"MONTHF"));
	var tMonth = parseInt(ds_cond.GetColumn(0,"MONTHT"));

	//조회기간: 0=같은달,1=2개월,2=3개월..
	if(((tYear-fYear)*12+tMonth-fMonth) > 11)
	{
		gfn_showMsg("CW00078"," 12 ");
		cb_montht.SetFocus();
		return;
	}

	ds_cond.SetColumn(0,"YYYYMMF",ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF"));
	ds_cond.SetColumn(0,"YYYYMMT",ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT"));

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "VEND_CODE", G_LGORT);
		ds_cond.SetColumn(0, "AREA_CODE", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "VEND_CODE", '');
		ds_cond.SetColumn(0, "AREA_CODE", G_AREA_CODE);
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "VEND_CODE", '');
		ds_cond.SetColumn(0, "AREA_CODE", G_AREA_CODE);
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "VEND_CODE", '');
		if(cb_type.Value = '2') {
			ds_cond.SetColumn(0, "AREA_CODE", cb_ara.Value);
		} else {
			ds_cond.SetColumn(0, "AREA_CODE", '');
		}	
	}

	tit_clearActionInfo();

	if(cb_type.Value = '1') {
		ds_cond.SetColumn(0, "GBN", '1');
		tit_addSearchActionInfo("cs32:CS3202001A_S1");
	} else if(cb_type.Value = '2') {
		tit_addSearchActionInfo("cs32:CS3202001A_S2");
	} else if(cb_type.Value = '3') {
		tit_addSearchActionInfo("cs32:CS3202001A_S3");
	} else if(cb_type.Value = '4') {
		ds_cond.SetColumn(0, "GBN", '4');
		tit_addSearchActionInfo("cs32:CS3202001A_S1");
	}

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	if (cb_type.Value = '1') {
		grd_list.Format = "Default0";
	} else if(cb_type.Value = '2') {
		grd_list.Format = "Default1";
	} else if(cb_type.Value = '3' || cb_type.Value = '4') {
		grd_list.Format = "Default";
	}
	ds_cond.SetColumn(0, "VEND_CODE", '');
	ds_cond.SetColumn(0, "AREA_CODE", '');

	if(cb_type.Value = '3') {
		grd_list.SetCellProp("body",1,"MASK","####.##"); 
	}	
	else {
		grd_list.SetCellProp("body",1,"MASK",""); 
	}

	ds_cond_copy.copy(ds_cond);
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	if(user_grp == '10') {
		ds_type.filter("CODE =='3'");
	} else if(user_grp == '20') {
		ds_type.filter("CODE =='2' OR CODE =='3'");
	} else if(user_grp == '30') {
		ds_type.filter("CODE =='2' OR CODE =='3'");
	} 
	cb_ara.Index = -1;
	cb_ara.Enable = false;
	cb_type.Index = 0;
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
