/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list2_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 현재 처리할 format 기억
		gfn_selectGridCol("grd_list2");
	} else {
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	gfn_saveToExcel(grd_list, true );
}
/********************************************************************************
* 기      능   : 그리드 DOUBLE CLICK시 상세조회
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	ds_cond2.SetColumn(0,"YYYYMMF","");
	ds_cond2.SetColumn(0,"YYYYMMT","");
	ds_cond2.SetColumn(0,"GBN","");
	ds_cond2.SetColumn(0,"BSU","");
	ds_cond2.SetColumn(0,"ARA","");
	ds_cond2.SetColumn(0,"BPM","");

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond2.SetColumn(0, "YYYYMMF", ds_list.GetColumn(nRow,"GUBUN"));
		ds_cond2.SetColumn(0, "BSU", G_LGORT);
		ds_cond2.SetColumn(0, "GBN", "A");
	} else if(user_grp == "20") { // 보수PM일 경우
		if(ds_cond_copy.GetColumn(0,"TYPE") == '2')
		{
			ds_cond2.SetColumn(0, "YYYYMMF", ds_cond_copy.GetColumn(0,"YYYYMMF"));
			ds_cond2.SetColumn(0, "YYYYMMT", ds_cond_copy.GetColumn(0,"YYYYMMT"));
			ds_cond2.SetColumn(0, "BSU", ds_list.GetColumn(nRow,"GUBUN2"));
			ds_cond2.SetColumn(0, "GBN", "C");
		}
		else if(ds_cond_copy.GetColumn(0,"TYPE") == '3')
		{
			ds_cond2.SetColumn(0, "YYYYMMF", ds_list.GetColumn(nRow,"GUBUN"));
			ds_cond2.SetColumn(0, "ARA", G_AREA_CODE);
			ds_cond2.SetColumn(0, "GBN", "B");
		} 
	} else if(user_grp == "30") { // 지방사무소일 경우
		if(ds_cond_copy.GetColumn(0,"TYPE") == '2')
		{
			ds_cond2.SetColumn(0, "YYYYMMF", ds_cond_copy.GetColumn(0,"YYYYMMF"));
			ds_cond2.SetColumn(0, "YYYYMMT", ds_cond_copy.GetColumn(0,"YYYYMMT"));
			ds_cond2.SetColumn(0, "BSU", ds_list.GetColumn(nRow,"GUBUN2"));
			ds_cond2.SetColumn(0, "GBN", "C");
		}
		else if(ds_cond_copy.GetColumn(0,"TYPE") == '3')
		{
			ds_cond2.SetColumn(0, "YYYYMMF", ds_list.GetColumn(nRow,"GUBUN"));
			ds_cond2.SetColumn(0, "ARA", G_AREA_CODE);
			ds_cond2.SetColumn(0, "GBN", "B");
		} 
	} else if(user_grp == "40") { // 고객지원부일 경우
		if(ds_cond_copy.GetColumn(0,"TYPE") == '1')
		{
			ds_cond2.SetColumn(0, "YYYYMMF", ds_cond_copy.GetColumn(0,"YYYYMMF"));
			ds_cond2.SetColumn(0, "YYYYMMT", ds_cond_copy.GetColumn(0,"YYYYMMT"));
			ds_cond2.SetColumn(0, "ARA", ds_list.GetColumn(nRow,"GUBUN2"));
			ds_cond2.SetColumn(0, "GBN", "D");
		}
		else if(ds_cond_copy.GetColumn(0,"TYPE") == '2')
		{
			ds_cond2.SetColumn(0, "YYYYMMF", ds_cond_copy.GetColumn(0,"YYYYMMF"));
			ds_cond2.SetColumn(0, "YYYYMMT", ds_cond_copy.GetColumn(0,"YYYYMMT"));
			ds_cond2.SetColumn(0, "BSU", ds_list.GetColumn(nRow,"GUBUN2"));
			ds_cond2.SetColumn(0, "GBN", "C");
		} 
		else if(ds_cond_copy.GetColumn(0,"TYPE") == '3')
		{
			ds_cond2.SetColumn(0, "YYYYMMF", ds_list.GetColumn(nRow,"GUBUN"));
			ds_cond2.SetColumn(0, "GBN", "E");
		} 
		else if(ds_cond_copy.GetColumn(0,"TYPE") == '4')
		{
			ds_cond2.SetColumn(0, "YYYYMMF", ds_cond_copy.GetColumn(0,"YYYYMMF"));
			ds_cond2.SetColumn(0, "YYYYMMT", ds_cond_copy.GetColumn(0,"YYYYMMT"));
			ds_cond2.SetColumn(0, "BPM", ds_list.GetColumn(nRow,"GUBUN2"));
			ds_cond2.SetColumn(0, "GBN", "F");
		} 
	}
	fn_query2();
}
/********************************************************************************
* 기      능   : grid double click 시 조회 
********************************************************************************/
function fn_query2() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs32:CS3202001A_S4", null, true);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list2=ds_list"
        , ""
        , "fn_afterQuery2"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list2.RowCount()+"");
}
/********************************************************************************
* 기      능   : 유형코드 변경시
********************************************************************************/
function cb_type_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == '2'  && user_grp == '40') {
		cb_ara.Enable = true;
		cb_ara.Index = 0;
	} else {
		cb_ara.Enable = false;
		cb_ara.Index = -1;
	}
}
