//=========================================================================================
// [ PART 5 ]
// Detail Process Event 정의 ( User Define function )
// 사용자 정의 함수는 ufn_xxxx() 형태로 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 디버그 문자열을 trace로 출력한다.
********************************************************************************/

function DEBUG(srcFunc, outStr)
{
	var outputString = "";

    outputString = "DEBUG " + 
				  this.title + " " + 
				  substr(getDate(), 8, 2) + ":" + 
				  substr(getDate(), 10, 2) + ":" + 
				  substr(getDate(), 12, 2) + ">";

	if(DEBUG_FLAG)
	{
		if(outStr.length == 0)
		{	
			outputString += srcFunc;
		}
		else
		{
			outputString += srcFunc + "() - " + outStr;
		}

		trace(outputString);
	}
}

/********************************************************************************
* 기      능   : 검색 조건들을 초기화 한다
********************************************************************************/
function ufn_initSearchParameters()
{	
	tit_clearActionInfo();					
	tit_addSearchActionInfo("cs32:CS3204001A_S1");					
	tit_callService("", "", "", "ds_cb_year=ds_cb_year", "", "", true, true, true);	

	cb_year.Index = 0;
	cb_month.Index = 0;
}


