/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol("grd_list");
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

function grd_sum_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	// 그리드 정렬 처리 
	gfn_sortGrid(obj,nCell);
}

function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(ds_list.count <= 0 || ds_list.currow < 0)
	{
		gfn_showAlert("CW10025", "견적을 선택하신 후 다시 시도해주세요.");
		return;
	}

	if(length(ds_list.GetColumn(ds_list.currow, "QTNUM")) < 10)
	{
		gfn_showAlert("CW10025", "견적번호가 존재하지 않아 견적상세 열람이 불가합니다.\n(2015년 1월 이전 작성 이동계획은 견적상세 열람 불가)");
		return;
	}

    var arg = "ar_qtnum='" + ds_list.GetColumn(ds_list.currow, "QTNUM") 
			+ "' ar_seq='" + ds_list.GetColumn(ds_list.currow, "SEQ")
			+ "' ar_upn='" + substr(ds_list.GetColumn(ds_list.currow, "MA_PSPID"), 0, 6)
			+ "' ar_cst='" + substr(ds_list.GetColumn(ds_list.currow, "MA_PSPID"), 6) + "'";

	var pop_obj = gfn_openPopup("CS0101008B", true, arg);
}
