	ds_cond.SetColumn(0,"HNO",hno);	
	ds_cond.SetColumn(0,"SEQ",seq);	
	ds_cond.SetColumn(0,"GND",gnd);		
	ds_cond.SetColumn(0,"ENDDATE","");		

	ed_pjt.Text = pjt+"-"+hno;

	// 2014.12.17 취소버튼 비활성화요청 by 최인식 과장.
	div_btn.btn_cancel.Enable = false;

	//인센티브 및 소급 적용 권한 제한 
	/*if(G_USER_ID == "2021200" || G_USER_ID == "2023687" || G_USER_ID == "2021153" || G_USER_ID == "T021153" // 승강기 담당 임유진
	|| G_USER_ID == "2019175" || G_USER_ID == "3021785" || G_USER_ID == "2018900" || G_USER_ID == "2029758") // 주차 담당 박준성 유성호
	{ 
		div_btn.btn_save.Enable = true;
	} else {
		div_btn.btn_save.Enable = false;
	}*/

	/*  저장버튼 권한 DB에서 참조되도록 변경. 2019.03.22 Han J.H */
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS3306001A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	btn_save_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

	if(btn_save_auth == true) {
		div_btn.btn_save.Enable = true;
	} else {
		div_btn.btn_save.Enable = false;
	}

	ufn_setInitHeader();
}
