	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true
        , true);
}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	gfn_clearMsgBar();

	if(chk_end.Value == true) {
		gfn_showMsg("CW00161");
		return;
	}

	var cnt = 0;
	ds_list.FireEvent=false;
	for(var i=0; i<ds_list.GetRowCount(); i++) {
		if(ds_list.GetColumn(i,"CHECK") == 1) {
			if(trim(t_gym) >= ds_list.GetColumn(i,"GYM")) {
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00179", arr);
				ds_list.FireEvent=true;
				return;
			} else {
				var strJar = ds_list.GetColumn(i,"BAM") + ds_list.GetColumn(i,"IY1") + ds_list.GetColumn(i,"IY2") + ds_list.GetColumn(i,"IY3") + ds_list.GetColumn(i,"IY4") + ds_list.GetColumn(i,"RET");
				ds_list.SetColumn(i,"JAR",strJar);
				ds_list.SetColumn(i,"FLAG","U");
				cnt++;
			}
		}
	}
	ds_list.FireEvent=true;	
	if(cnt==0){
		//alert("선택된 호기가 없습니다.");
		gfn_showAlert("CW00013");
	}
	else{
		ufn_136Update();
	}
}
