/********************************************************************************
* 기      능   : 취소 35
********************************************************************************/
function fn_cancel() {

	// 2014.12.17 취소기능없애주세요 by 최인식과장.
	return;

	gfn_clearMsgBar();
	ds_list2.ClearData();
	ds_list3.ClearData();

	var t_cnt = 0;

	if(chk_flag == false) {
		gfn_showMsg("CW00178");
		return;
	}

	if(ds_list.FindRow("CHECK", 1) == -1) {
		gfn_showMsg("CW00013");
		return;
	}

	// 20130927 김재순CJ 요청(서비스관리부 이세희만 발주중지등록 가능하도록)
	if(G_USER_ID == "2018454" || G_USER_ID == "T018454" 
	|| G_USER_ID == "2021153" || G_USER_ID == "T021153"  //임유진-고객지원부 
	|| G_USER_ID == "2017938" || G_USER_ID == "T017938" 
	|| G_USER_ID == "2023687" || G_USER_ID == "2021200"  || G_USER_ID == "2018900" || G_USER_ID == "2029758" 
	|| G_USER_ID == "2018519" || G_USER_ID == "3014312" 
	|| G_USER_ID == "3016544" || G_USER_ID == "2019175" || G_USER_ID == "3018679") {
		//alert("OK");
	} else {
		gfn_showAlert("무상발주중지 작업은 서비스관리부 권한입니다. 서비스관리부에 문의 바랍니다.");
		return false;
	}

	if(chk_end.value == false || length(trim(cal_end.value)) == 0) {
		gfn_showMsg("CW00160");
		return;
	}

	if(ds_cond.GetColumn(0,"BGT") > ds_cond.GetColumn(0,"ENDDATE")) {
		gfn_showAlert("CW00163");
		for(var i=0; i<ds_list.GetRowCount(); i++) {
			ds_list.SetColumn(i, "CHECK", false);
		}
		return;
	}

	if(ds_cond.GetColumn(0,"BHD") <= ds_cond.GetColumn(0,"ENDDATE")) {
		gfn_showAlert("CW00164");
		for(var i=0; i<ds_list.GetRowCount(); i++) {
			ds_list.SetColumn(i, "CHECK", false);
		}
		return;
	}

	for(var i=0; i<ds_list.GetRowCount(); i++) {
/*
		if(ds_list.GetColumn(i, "PST") > "A1") {
			t_cnt++;
		}
*/
		if(ds_list.GetColumn(i,"CHECK") == 1) {
			var strJar = ds_list.GetColumn(i,"BAM") + ds_list.GetColumn(i,"IY1") + ds_list.GetColumn(i,"IY2") + ds_list.GetColumn(i,"IY3") + ds_list.GetColumn(i,"IY4") + ds_list.GetColumn(i,"RET");
			ds_list.SetColumn(i, "JAR", strJar);
			ds_list.SetColumn(i, "FLAG", "U");
			ds_list.SetColumn(i, "MG1", mg1);
			ds_list.SetColumn(i, "ENDDATE", cal_end.value);

			if(ds_list.GetColumn(i, "GND") == "A") { //무상공사
				if(ds_list.GetColumn(i, "SEQ") == "01") {
					ds_list.SetColumn(i, "GBN", "A");
				} else {
					ds_list.SetColumn(i, "GBN", "B");
				}
			} else if(ds_list.GetColumn(i, "GND") == "B") { //무상일반
				if(ds_list.GetColumn(i, "SEQ") == "01") {
					ds_list.SetColumn(i, "GBN", "C");
				} else {
					ds_list.SetColumn(i, "GBN", "D");
				}
			}

			if(substr(cal_bhd.value,0,6) == substr(cal_end.value,0,6)) {
				if(ds_list.GetColumn(i, "JYM") == substr(cal_end.value,0,6)) {
					//ds_list.SetColumn(i, "IDQ", parseInt(substr(cal_bhd.value,6,2)) - parseInt(substr(cal_end.value,6,2)));
					ds_list.SetColumn(i, "IDQ", parseInt(substr(cal_end.value,6,2)));
					//ds_list.SetColumn(i, "BAM", parseInt(substr(cal_end.value,6,2)));

					var rows2 = ds_list2.AddRow();
					ds_list2.CopyRow(rows2, "ds_list", i);
				} else {
					var rows3 = ds_list3.AddRow();
					ds_list3.CopyRow(rows3, "ds_list", i);
				}
			} else {
				if(ds_list.GetColumn(i, "JYM") == substr(cal_end.value,0,6)) {
					ds_list.SetColumn(i, "IDQ", parseInt(substr(cal_end.value,6,2)));

					var rows2 = ds_list2.AddRow();
					ds_list2.CopyRow(rows2, "ds_list", i);
				} else {
					var rows3 = ds_list3.AddRow();
					ds_list3.CopyRow(rows3, "ds_list", i);
				}
			}
		}
	}

	var rows4 = ds_list4.AddRow();

	if(ds_list2.GetRowCount() > 0) {
		ds_list4.CopyRow(rows4, "ds_list2", 0);
	} else {
		ds_list4.CopyRow(rows4, "ds_list3", 0);
	}

	if(t_cnt > 0) {
		gfn_showAlert("CW00159");
		return;
	}

	if(ds_list.rowCount() < 1) {
		// 승인할 정보 없음. 
		return;
	}

	if(substr(cal_end.value,6,8) == "01" && cal_bgt.value == cal_end.value) { //발주시작일(CS116_BGT) == 입력한 중도해지일
		/* 이전년도 발주일자 불가(EIS 장표 대수 오류) */
		var bYear = substr(gfn_getBeforeMonth(substr(cal_end.value,0,6),1),0,4);
		var cYear = substr(gfn_getCurrDate(),0,4);

//		if(bYear < cYear) {
//			gfn_showAlert("이전년도로 무상해지가 불가능합니다.\n고객지원부에 문의바랍니다.(02-3670-0816)");
//			return;
//		}
	}

	if(gnd == "B") { //무상일반일 경우
		/* 이전년도 발주일자 불가(EIS 장표 대수 오류) */
		var bYear = substr(cal_end.value,0,4);
		var cYear = substr(gfn_getCurrDate(),0,4);

//		if(bYear < cYear) {
//			gfn_showAlert("이전년도로 무상해지가 불가능합니다.\n고객지원부에 문의바랍니다.(02-3670-0816)");
//			return;
//		}
	}

	tit_clearActionInfo();

	if(cal_bgt.value == cal_end.value) { //발주시작일(CS116_BGT) == 입력한 중도해지일
		// 기성비지급정보 update(ZCST136)
		tit_addSaveActionInfo(
			""
			, "cs33:CS3306001A_U2"
			, ""
			, "FLAG");

		if(substr(cal_end.value,6,8) == "01") {
			// 무상보수발주정보 update(ZCST116)
			tit_addSaveActionInfo(
				""
				, "cs33:CS3306001A_U8"
				, ""
				, "FLAG");
		} else {
			// 무상보수발주정보 update(ZCST116)
			tit_addSaveActionInfo(
				""
				, "cs33:CS3306001A_U6"
				, ""
				, "FLAG");
		}
/*
		// 무상보수발주정보 delete(ZCST116)
		tit_addSaveActionInfo(
			""
			, "cs33:CS3306001A_D1"
			, ""
			, "FLAG");
*/
		// 무상보수발주정보 select(ZCST116)
		tit_addSearchActionInfo("cs33:CS3306001A_S3", true);

		// 보수대상호기마스터 update(ZCST111)
		tit_addSaveActionInfo(
			""
			, "cs33:CS3306001A_U3"
			, ""
			, "FLAG");
	} else { //발주시작일(CS116_BGT) <> 입력한 중도해지일
		// 기성비지급정보 update(ZCST136)
		tit_addSaveActionInfo(
			""
			, "cs33:CS3306001A_U4"
			, ""
			, "FLAG");

		// 기성비지급정보 update(ZCST136)
		tit_addSaveActionInfo(
			""
			, "cs33:CS3306001A_U5"
			, ""
			, "FLAG");

		// 무상보수발주정보 update(ZCST116)
		tit_addSaveActionInfo(
			""
			, "cs33:CS3306001A_U6"
			, ""
			, "FLAG");

		// 보수대상호기마스터 update(ZCST111)
