}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리 
	gfn_showSearchMsg(grd_list);

	if(ds_list.RowCount() == 0) {
		chk_end.Enable = false;
	} else {
		chk_end.Enable = true;
	}

	t_gym = "000000";
	t_pst = "";

	for(var i=0; i<ds_list.GetRowCount(); i++) {
		if(trim(ds_list.GetColumn(i, "PST")) == "A6") {
			if(trim(t_gym) < trim(ds_list.GetColumn(i, "GYM"))) {
				t_gym = trim(ds_list.GetColumn(i, "GYM"));
			}
			t_pst = trim(ds_list.GetColumn(i, "PST"));
		}

		if(trim(t_pst) <> "A6") {
			t_gym = gfn_getBeforeMonth(trim(ds_list.GetColumn(0, "JYM")), 1);
		}
	}
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}
/********************************************************************************
* 기      능   : 취소 Callback  
********************************************************************************/
function fn_afterCancel(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}
