function ufn_setInitHeader(){	
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs33:CS3306001A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_condData=ds_condData"
        , ""
        , "ufn_afterSetInitHeader"
        , true
        , true
        , true);	
}
function ufn_afterSetInitHeader(){
	if(ds_condData.GetRowCount()>0){
		ds_cond.Copy(ds_condData);		
	}
	else{
		ds_cond.SetColumn(0,"GNDTEXT","");
		ds_cond.SetColumn(0,"BGT","");
		ds_cond.SetColumn(0,"BHD","");			
	}
	fn_query();
}
