/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID != "CHECK") {
		ds_list.FireEvent=false;
		ds_list.SetColumn(nRow,"CHECK","1");
		ds_list.FireEvent=true;	
	}
}

function chk_end_OnClick(obj,strValue)
{
	var t_cnt = 0;
/*	
	for(var i=0; i<ds_list.GetRowCount(); i++) {
		if(ds_list.GetColumn(i, "PST") > "A1") {
			t_cnt++;
		}
	}
*/
	if(t_cnt > 0) {
		gfn_showAlert("CW00159");
		chk_end.Value = false;
	} else {

		if(obj.value = true) {
			cal_end.Enable = true;
		} else {
			cal_end.Enable = false;
			cal_end.Value = "";
		}
	}
}

function cal_end_OnChanged(obj,strOldText,strNewText)
{
	gfn_clearMsgBar();

	var t_date = "";

	if(ds_cond.GetColumn(0,"BGT") > ds_cond.GetColumn(0,"ENDDATE")) {
		gfn_showAlert("CW00163");
		for(var i=0; i<ds_list.GetRowCount(); i++) {
			ds_list.SetColumn(i, "CHECK", false);
			chk_flag = false;
		}
		return;
	}

	if(ds_cond.GetColumn(0,"BHD") <= ds_cond.GetColumn(0,"ENDDATE")) {
		gfn_showAlert("CW00164");
		for(var i=0; i<ds_list.GetRowCount(); i++) {
			ds_list.SetColumn(i, "CHECK", false);
			chk_flag = false;
		}
		return;
	}

	if(obj.value <> "") {
		t_date = substr(obj.value,0,6);

		for(var i=0; i<ds_list.GetRowCount(); i++) {
			if(t_date <= ds_list.GetColumn(i, "JYM")) {
				if((ds_list.GetColumn(i, "PST") > "A1" && ds_cond.GetColumn(0,"ENDDATE") <> gfn_getLeapLastDay(ds_cond.GetColumn(0,"ENDDATE")))
				   || (ds_list.GetColumn(i, "PST") > "A1" && ds_cond.GetColumn(0,"ENDDATE") == gfn_getLeapLastDay(ds_cond.GetColumn(0,"ENDDATE")) && t_date < ds_list.GetColumn(i, "JYM"))) {
					var arr = [parseInt(i) + 1];
					gfn_showMsg("CW00177", arr);
					for(var i=0; i<ds_list.GetRowCount(); i++) {
						ds_list.SetColumn(i, "CHECK", false);
					}
					chk_flag = false;
					return;
				} else {
					ds_list.SetColumn(i, "CHECK", true);
					chk_flag = true;
				}
			} else {
				ds_list.SetColumn(i, "CHECK", false);
				chk_flag = false;
			}
		}
	} else {
		for(var i=0; i<ds_list.GetRowCount(); i++) {
			ds_list.SetColumn(i, "CHECK", false);
		}
		chk_flag = false;
	}
}

function btn_copy_OnClick(obj)
{
	var arg = "pjt=" + quote(pjt) +
			  " hno=" + quote(hno) +
			  " seq=" + quote(seq) +
			  " gnd=" + quote(gnd);

	gfn_openPopup("CS3306001C", true, arg);
}
