var str = "";
var seq = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

    var codeArr = ["CS188","CS189","CS190"];
    var dsObjArr = [ds_misutp,ds_const,ds_bdst];
    var addTypeArr = [G_CODE_SELECT,G_CODE_SELECT,G_CODE_SELECT];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr, true);

	var t_date = gfn_getAfterMonth(substr(gfn_getCurrDate(),0,6), 1);
	var curYear = substr(t_date,0,4);
	fn_makeYear(curYear,-5,5);
	cb_month.index = parseInt(substr(t_date,4,2))-1;
	cb_year.SetFocus();
//	cal_sdat.value = gfn_getLeapLastDay(gfn_getCurrDate());
	cal_sdat.value = gfn_getCurrDate();
	cal_cdat.value = gfn_getLeapLastDay(gfn_getBeforeMonth(substr(gfn_getCurrDate(),0,6), 3) + "01");
	str = "";
	cnt = 0;
}

/************************************************************************************
* 기      능   : 작업기간 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
************************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_year_cd.AddRow();
		ds_year_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_year.index = eNum;
	cb_gbn.Index = 0;
}

