* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	gfn_clearMsgBar();

	var user_grp = gfn_getUserGroup();

	ds_cond.SetColumn(0, "MANDT", G_MANDT);
	if(cb_gbn.Value == "A") {
		ds_cond.SetColumn(0, "SDAT", gfn_getCurrDate());
	} else {
		ds_cond.SetColumn(0, "SDAT", cal_sdat.value);
	}

	ds_cond.SetColumn(0, "SYY", substr(cal_sdat.value,0,4));
	ds_cond.SetColumn(0, "SYYMM", substr(cal_sdat.value,0,6));
	ds_cond.SetColumn(0, "CDAT", cal_cdat.value);
	ds_cond.SetColumn(0, "CYYMM", gfn_getBeforeDate(cal_cdat.value, 364));
	ds_cond.SetColumn(0, "YYMM", ds_cond.GetColumn(0,"YY") + ds_cond.GetColumn(0,"MM"));
	ds_cond.SetColumn(0, "PROJ", ed_upn.value);

	var t_sql = "cs34:CS3403001A_S1";

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		//====================================수정시작=======================================	
		/*
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');		
		}
		*/
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else if(div_code.cb_big.value == '3') { // 협력사 콤보1이 팀 
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else { // 협력사 콤보1이 부서일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_small.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", substr(trim(div_code.cb_mid.value),0,2));
			ds_cond.SetColumn(0, "BSU", '');
		}		
		//====================================수정끝=======================================				

	}
	if(ds_cond.GetColumn(0,"THYEAR") == "" || ds_cond.GetColumn(0,"THYEAR") == "0") {
		ds_cond.SetColumn(0,"THYEAR", "0");
	}

	if(length(trim(ds_cond.GetColumn(0, "BSU"))) > 0) { //보수협력사가 선택 됐을 경우
		t_sql = "cs34:CS3403001A_S2";
	}
	ds_cond.SetColumn(0, "LIFNR", div_code.LIFNR.Text);

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list_E);
	ds_list.row = -1;
	ds_list.ClearData();

	// HTTP SESSION TIMEOUT을 변경
	http.TimeOut = 1800;

	tit_clearActionInfo();
	tit_addCsvSearchActionInfo(t_sql);
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);

/*
	tit_callService(
        "CS3403001A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery");
*/
}

