/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_E_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		// 전체 선택
		gfn_selectAllGrid(obj,0,0,true);
	} else {
		// 그리드 정렬 처리
		gfn_sortGrid(obj,nCell);
	}
}

function cb_year_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	var t_date = gfn_getBeforeMonth(cb_year.value + cb_month.value, 1);

	cal_cdat.value = gfn_getLeapLastDay(gfn_getBeforeMonth(t_date, 3) + "01");

	if(cb_gbn.Value == "A") {
		cal_sdat.Value = gfn_getCurrDate();
	} else {
		cal_sdat.Value = gfn_getLeapLastDay(t_date + "01");
	}
}

function cb_month_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	var t_date = gfn_getBeforeMonth(cb_year.value + cb_month.value, 1);

	cal_cdat.value = gfn_getLeapLastDay(gfn_getBeforeMonth(t_date, 3) + "01");

	if(cb_gbn.Value == "A") {
		cal_sdat.Value = gfn_getCurrDate();
	} else {
		cal_sdat.value = gfn_getLeapLastDay(t_date + "01");
	}
}

/********************************************************************************
* 기      능   : 저장 30
********************************************************************************/
function fn_save() {
	var t_yymm = ds_cond.GetColumn(0,"YY") + ds_cond.GetColumn(0,"MM");
	var t_date = ds_cond.GetColumn(0,"YY") + ds_cond.GetColumn(0,"MM");
	gfn_clearMsgBar();

// 2011.10.05 최인식 대리 요청
//	if(ds_list.rowCount() < 1 || gfn_getAfterMonth(substr(gfn_getCurrDate(),0,6),1) <> t_yymm) {
	if(ds_list.rowCount() < 1) {
		// 저장할 정보 없음.
		gfn_showMsg("CW00010");
		return;
	}

	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return;
	} else {
		for(var i=0; i<ds_list.RowCount(); i++) {
			if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
				ds_list.SetColumn(i, "FLAG", "U");
				ds_list.SetColumn(i, "AMT_MM", ds_list.GetColumn(i, "MI_SU_F"));
				//개행문자 공백처리
				ds_list.SetColumn(i, "INGTEXT", REPLACE(REPLACE(ds_list.GetColumn(i, "INGTEXT"), CHR(10), ''), CHR(13), '')  );

				if(length(replace(trim(ds_list.GetColumn(i, "PLDAT")),".","")) < 8) {
					var arr = [parseInt(i) + 1, "수금계획일"];
					gfn_showMsg("CW00055", arr);
					return;
				}

				if(length(trim(ds_list.GetColumn(i, "PLDAT"))) == 0 || ds_list.GetColumn(i, "PLDAT") == "00000000") {
					var arr = [parseInt(i) + 1, "수금계획일"];
					gfn_showMsg("CW00055", arr);
					return;
				}
/* 2011.10.05 최인식 대리 요청
				if(substr(ds_list.GetColumn(i, "PLDAT"),0,6) < t_date) {
					var arr = [parseInt(i) + 1, "수금계획일"];
					gfn_showMsg("CW00055", arr);
					return;
				}
*/
				// 2020.01.06 ZFIT1014 Insert 시 Null로 인한 예외처리. Han JH
				if(length(trim(ds_list.GetColumn(i, "AMT_MM"))) == 0) {
					var arr = [parseInt(i) + 1, "수금계획금액"];
					gfn_showMsg("CW00055", arr);
					return;
				}

				if(ds_list.GetColumn(i, "MI_SU_F") < ds_list.GetColumn(i, "AMT_MM")) {
					var arr = [parseInt(i) + 1, "수금계획금액"];
					gfn_showMsg("CW00055", arr);
					return;
				}

				if(length(trim(ds_list.GetColumn(i, "MISUTP"))) == 0) {
					var arr = [parseInt(i) + 1, "장기미수사유"];
					gfn_showMsg("CW00055", arr);
					return;
				}

				if(length(trim(ds_list.GetColumn(i, "CONST"))) == 0) {
					var arr = [parseInt(i) + 1, "계약상태"];
					gfn_showMsg("CW00055", arr);
					return;
				}

				if(length(trim(ds_list.GetColumn(i, "BDST"))) == 0) {
					var arr = [parseInt(i) + 1, "건물상태"];
					gfn_showMsg("CW00055", arr);
					return;
				}
			}
		}

		tit_callService(
			"CS3403001A_ACT"
			, "save"
			, "ds_list=ds_list"
			, ""
			, ""
			, "fn_afterSave");
	}
}

/********************************************************************************
* 기      능   : 취소 35
********************************************************************************/
function fn_cancel() {
	gfn_clearMsgBar();
	str = "";
	cnt = 0;

	if(ds_list.rowCount() < 1) {
		// 취소할 정보 없음.
		gfn_showMsg("CW00010");
		return;
	} else {
		ds_list.reset();
	}
}

/********************************************************************************
* 기      능   : 계산 70
********************************************************************************/
function fn_cal() {
	var t_cnt = 0;
	var t_yymm = ds_cond.GetColumn(0,"YY") + ds_cond.GetColumn(0,"MM");
	var t_date = ds_cond.GetColumn(0,"YY") + ds_cond.GetColumn(0,"MM") + "01";
	t_date = gfn_getLeapLastDay(t_date);

	gfn_clearMsgBar();

	if(ds_list.rowCount() < 1 || gfn_getAfterMonth(substr(gfn_getCurrDate(),0,6),1) <> t_yymm) {
//	if(ds_list.rowCount() < 1) {
		// 계산할 정보 없음.
		gfn_showMsg("CW00010");
		return;
	}

	gfn_selectAllGrid(grd_list_E,0,0,true);

	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true && (ds_list.GetColumn(i, "PLDAT") == "00000000" || ds_list.GetColumn(i, "PLDAT") < t_date) && ds_list.GetColumn(i, "MI_SU_F") >= ds_list.GetColumn(i, "AMT_MM")) {
//		if(ds_list.GetColumn(i, "CHK") == true) {
			ds_list.SetColumn(i, "FLAG", "U");


			//개행문자 공백처리
			ds_list.SetColumn(i, "INGTEXT", REPLACE(REPLACE(ds_list.GetColumn(i, "INGTEXT"), CHR(10), ''), CHR(13), '')  );

			if(t_yymm == substr(ds_list.GetColumn(i, "PLDAT"),0,6)) {
				ds_list.SetColumn(i, "PLDAT", ds_list.GetColumn(i, "PLDAT"));
				ds_list.SetColumn(i, "AMT_MM", ds_list.GetColumn(i, "AMT_MM"));
			} else {
				ds_list.SetColumn(i, "PLDAT", t_date);
				ds_list.SetColumn(i, "AMT_MM", ds_list.GetColumn(i, "MI_SU_F"));
			}

			t_cnt++;
		}
/*
		if(ds_list.GetColumn(i, "MI_SU_F") < ds_list.GetColumn(i, "AMT_MM")) {
			var arr = [parseInt(i) + 1, "수금계획금액"];
			gfn_showMsg("CW00055", arr);
			ds_list.Reset();
			return;
		}
*/
	}

	if(t_cnt == 0) {
		// 계산할 정보 없음.
		var arr = ["계산용"];
		gfn_showMsg("CW00182", arr);
		ds_list.Reset();
		return;
	}

	tit_callService(
		"CS3403001A_ACT"
		, "save"
		, "ds_list=ds_list"
		, ""
		, ""
		, "fn_afterSave");
}

function fn_afterSave(errCode, errMsg) {
	str = "";
	cnt = 0;

	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

