function fn_TempSave(seq)
{   
	tit_clearActionInfo();
	tit_addSingleActionInfo("cs34:CS3403001A_D1");
	tit_addMultiActionInfo("cs34:CS3403001A_I1");
	tit_callService(
        ""
        , ""
        , "ds_list_cp=ds_list_cp"
        , ""
        , "seq=" + quote(seq)
        , "fn_PopUp(" + quote(seq) + ")");

}
/********************************************************************************
* 기      능   : 프로젝트 관련 args 값 넘기기 및 팝업
********************************************************************************/
function fn_PopUp(seq)
{		
	var arg = "upn=" + quote(substr(ds_list_cp.GetColumn(ds_list_cp.firstrow,"PSPID"),0,6))
		   + " ps_pspid=" + quote(ds_list_cp.GetColumn(ds_list_cp.firstrow,"PSPID")) 
		   + " vkgrp=" + quote(ds_list_cp.GetColumn(ds_list_cp.firstrow,"VKGRP"))
		   + " lgort=" + quote(ds_list_cp.GetColumn(ds_list_cp.firstrow,"LGORT"))
		   + " kunnr=" + quote(ds_list_cp.GetColumn(ds_list_cp.firstrow,"KUNNR"))
		   + " kunnr_nm=" + quote(ds_list_cp.GetColumn(ds_list_cp.firstrow,"KUNNR_NM"))
		   + " seq=" + quote("0"+seq)
		   + " amount=" + quote(ds_list_cp.Sum("MI_SU_F"))
		   + " bstnk=" + quote(ds_list_cp.GetColumn(ds_list_cp.firstrow, "BSTNK"))
		   + " dno_01=" + quote(ds_list_cp.GetColumn(ds_list_cp.firstrow, "DNO_01"))
		   + " dno_02=" + quote(ds_list_cp.GetColumn(ds_list_cp.firstrow, "DNO_02"))
		   + " dno_03=" + quote(ds_list_cp.GetColumn(ds_list_cp.firstrow, "DNO_03"));	

	gfn_openPopup("CS4203001A", true, arg);	
		//gfn_openPopup("CS3403001B", true, arg);	사용안함
}
/********************************************************************************
* 기      능   : 동일 프로젝트 여부 체크
* 201408 hr.lee
* 동일 wbs >> 동일 통합프로젝트 / 동일 제품군/ 동일 고객 일 경우
* 일괄 작성 가능
********************************************************************************/
function fn_PspidCheck()
{
	ds_list_cp.ClearData();

	if ( ds_list.rowCount() < 1 ) {
		// 정보 없음
		gfn_showMsg("CW00010");
		return;
	}

	if(ds_list.FindRow("CHK", 1) == -1) {
		// 처리할 정보 선택
		gfn_showMsg("CW00013");
		return;
	}

	for(var i=0; i<ds_list.RowCount(); i++) {

		if(ds_list.GetColumn(i, "CHK") == true) { 

			var rows = ds_list_cp.AddRow();

			if(rows  == 0){		

				ds_list_cp.CopyRow(rows,"ds_list",i);	

			} else {
				// ds_list_cp 의 시작 행과 프로젝트 번호 비교. 같은 프로젝트 번호에 해당하는 건수에 한하여 공문 출력
				if(substr(ds_list_cp.GetColumn(ds_list.firstrow,"PSPID"),0,6) <> substr(ds_list.GetColumn(i,"PSPID"),0,6)){
					alert(i + 1 + "번째 행의 프로젝트번호를 확인하십시오");
					return false;			
				} 	
				if(ds_list_cp.GetColumn(ds_list.firstrow,"KUNNR") <> ds_list.GetColumn(i,"KUNNR")){		
					alert(i + 1 + "번째 행의 고객명을 확인하십시오");
					return false;				
				}  			
			/*	if(ds_list_cp.GetColumn(ds_list.firstrow,"AUART") <> ds_list.GetColumn(i,"AUART")){	
					alert(ds_list_cp.GetColumn(ds_list.firstrow,"AUART"));
					alert(ds_list.GetColumn(i,"AUART"));	
					alert(i + 1 + "번째 행의 제품군을 확인하십시오");
					return false;				
				} 
			*/	
				ds_list_cp.CopyRow(rows,"ds_list",i);
			}
		}
	}
	return true;
}

