function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	var firstDay = substr(gfn_getCurrDate(),0,6) + "01";
	cal_stada.value = firstDay;
	cal_endda.value = gfn_getCurrDate();

	ds_cond.SetColumn(0, "STADA", cal_stada.value);
	ds_cond.SetColumn(0, "ENDDA", cal_endda.value);
    cb_spart.index = 0;
    btn_save.SetFocus();
 //   cb_spart.Enable = true;

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-2,2);
	cb_mm.index =  parseInt(substr(gfn_getCurrDate(),4,2))-1;

	////기성매출 업로드 작업 권한 제한
	// if(G_USER_ID == "2021056" or G_USER_ID == "2026988" or    // 전현정 이상혁
	   // G_USER_ID == "2017750" or G_USER_ID == "2018900" or    // 이주형 방기태
	   // G_USER_ID == "2029758" or G_USER_ID == "2999999") {    // 한정훈 김종남
		// btn_upload.Visible = true;
	// }
	//================================기성매출 업로드 작업 권한 제한=================
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS3404001A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	upload_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();	
	if (upload_auth){
		btn_upload.Visible = true;
	}
	//===============================================================================
}
/************************************************************************************
* 기      능   : 작업기간 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
************************************************************************************/
function fn_makeYear(curYear,sNum,eNum) {
	var rowCnt = eNum-sNum;
	var row = 0;
	for(var i=rowCnt; i>-1; i--) {
		ds_yyyy.AddRow();
		ds_yyyy.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yyyy.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_yyyy.index = eNum;
}

