//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	if(ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		// error 정보 표시 
		gfn_openWSErrorWin(grd_list, ds_list, ds_error, "FLAG");
	} else {
		if(errCode != 0) {
			gfn_showMsg("CE00001");
		} else {
			gfn_showMsg("CI00002", ds_list.RowCount() + "");
		}
	}
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(ds_error2.rowcount() > 0) {
		gfn_showMsg("CE00001");
		// error 정보 표시 
//		gfn_openWSErrorWin(grd_list, ds_list1, ds_error2, "FLAG");
//        alert(errMsg);
		gfn_showalert(ds_error2.GetColumn(0, "ERRMSG"));

	} else {
		if(errCode != 0) {
			gfn_showMsg("CE00001");
		} else {
			gfn_showMsg("CI00005");
			fn_query();
		}
	}
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave1(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}
