function btn_jobend_OnClick(obj) {
	gfn_clearMsgBar();

	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showAlert("CW00013");		//처리할 자료를 먼저 선택해 주십시오.
		return false;
	}

	if(!gfn_showConfirm("CW10207","작업완료")) {
		return;
	}

	if(ds_list.rowCount() < 1) {
		// 저장할 정보 없음.
		gfn_showMsg("CW00010");
		return;
	}

	if(!gfn_isChangeDs(ds_list, true)) {
		return;
	}

	ds_list2.ClearData();

	var j = 0;
	// 사용자 변경 여부
	var arr = split(G_ORG_USER_INFO, "^", true);

	// 수리부품 청구계획 변경요청 상태에서는 변경완료되기 전까지는 작업완료저장 버튼을 누를수 없도록 Validation 로직 추가
	// 2019.06.14 Han J.H
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) {
			if(ds_list.GetColumn(i, "STATUS") == "A") {
				gfn_showAlert("변경요청 상태에서는 작업완료저장 로직을 수행 할 수 없습니다.\n" + "S/O : " + ds_list.GetColumn(i, "VBELN"));
				return false;
			}		
		}
	}

	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true && ds_list.GetColumn(i, "STATUS") == ""){
			ds_list2.AddRow();
			ds_list2.SetColumn(j, "FLAG", "U");
			if(ds_list.GetColumn(i, "JOBEND") == 0) ds_list2.SetColumn(i, "JOBEND", '');
			else ds_list2.SetColumn(j, "JOBEND", ds_list.GetColumn(i, "JOBEND"));
			ds_list2.SetColumn(j, "POSID", ds_list.GetColumn(i, "POSID"));
			ds_list2.SetColumn(j, "G_ORG_USER_ID", arr[0]) ;   // 사용자 변경 여부
			j++;
		}			
	}

	// if(ds_list2.RowCount() == 0) {
		// gfn_showAlert("작업완료 처리 대상이 없습니다.");
		// return false;
	// }

	tit_clearActionInfo();
	// ZCST001-JOBEND 변경 처리
   	tit_addSaveActionInfo(
		""
		, "cs34:CS3404001A_U1"
		, ""
		, "FLAG");
	// ZCST013 작업완료 작업 LOG	20150707 (전현정 요청)
	tit_addMultiActionInfo("cs34:CS3404001A_I1");

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterSave2"
		, true);
}
