	} else {
		gfn_showMsg("CI00005");
	}
}
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	//문서관리번호 신규추가시 NULL , 수정시 저장된 해당 문서관리번호
	var a="";  
	//오더유형코드 //1:정기보수 //2:수리공사 //3:부품판매
	var b="1";
	//문서구문코드 //1:계약서 //2:발주서 //3:계약변경합의서 //4:작업완료확인서 //5:추가약정서 
	//var c=ds_list.GetColumn(nRow, "CS655_DKN"); 

	var arg = "";
	arg = " fa_docno="+quote(ds_list.GetColumn(nRow, "POSID"));
	arg = arg +" fa_oknno="+quote(b);


	var rtn_doc = gfn_openPopup2("CS9615001C", true , arg);
	if(length(ds_list.GetColumn(nRow, "CS655_DOC"))<1 && length(rtn_doc)>10) {
		ds_doc.ClearData();
		ds_doc.AddRow();																
		ds_doc.SetColumn(0,"POSID",ds_list.GetColumn(nRow, "POSID"));
		ds_doc.SetColumn(0,"DNO",rtn_doc);	

		tit_clearActionInfo();

		//저장 처리 
		tit_addMultiActionInfo("cs34:CS3404001A_U2");
		tit_callService(
			""
			, ""
			, "ds_doc=ds_doc:A"
			, ""
			, ""
			, ""
			, true
			, true);
	}
}

