var input_auth01 = false;
var executeChk = false;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	cal_gijun.value = gfn_getCurrDate();

	ds_cond.SetColumn(0, "GIJUN", cal_gijun.value);
	cb_auart.index = 0;
    cb_spart.index = 0;
//    btn_save.SetFocus();
 //   cb_spart.Enable = true;

	// C청구 사유 
	var codeArr = ["CS201"];
    var dsObjArr = [ds_cbill_c];
    var addTypeArr = [G_CODE_SELECT];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

    //====================마감권한 공통함수로 변경=====================
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS3405001A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	input_auth01 = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();    


	// C청구 마감 버튼 활성화 대상 - 최인식 전현정 조성인 이준석
	/*
	if(G_USER_ID <> "2017938" && G_USER_ID <> "2021056"  && G_USER_ID <> "2027607"  && G_USER_ID <> "2020971") {
		btn_save.Enable = false;
	}
	*/

	if(input_auth01 == true){
		btn_save.Enable = true;
	} else {
		btn_save.Enable = false;
	}

}

