//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	if(ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		// error 정보 표시 
		gfn_openWSErrorWin(grd_list, ds_list, ds_error, "FLAG");
	} else {
		if(errCode != 0) {
			gfn_showMsg("CE00001");
		} else {
			gfn_showMsg("CI00002", ds_list.RowCount() + "");
		}
	}
	executeChk = false;
}
/********************************************************************************
* 기      능   : 저장 30
********************************************************************************/
function fn_save() {
	gfn_clearMsgBar();

	if(ds_list.rowCount() < 1) {
		// 저장할 정보 없음.
		gfn_showMsg("CW00010");
		return;
	}

	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return;
	}

	if(!gfn_isChangeDs(ds_list, true)) {
		return;
	}

	ds_list1.ClearData();

	var j = 0;
	ds_list.FireEvent = false;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
		    if(length(trim(ds_list.GetColumn(i, "CBILL_C"))) == 0) {
					gfn_showAlert(i + 1 + "번째 프로젝트의 사유를 입력하세요.");
					return false;
			}

		    if(trim(ds_list.GetColumn(i, "CBILL_C")) < 20) { //미사용코드로 저장하려고 할때 경고 메세지
					gfn_showAlert(i + 1 + "번째 프로젝트의 사유는 유효하지 않습니다.");
					return false;
			}

			ds_list1.AddRow();
			ds_list1.SetColumn(j, "FLAG", "U");
//			ds_list1.SetColumn(j, "YYMM", ds_list.GetColumn(i, "YYMM"));
			ds_list1.SetColumn(j, "VBELN", ds_list.GetColumn(i, "VBELN"));
			ds_list1.SetColumn(j, "PSPID", ds_list.GetColumn(i, "PSPID"));
			ds_list1.SetColumn(j, "ARA", ds_list.GetColumn(i, "ARA"));
			ds_list1.SetColumn(j, "VKGRP", ds_list.GetColumn(i, "VKGRP"));
			ds_list1.SetColumn(j, "VKGRP_NM", ds_list.GetColumn(i, "VKGRP_NM"));
			ds_list1.SetColumn(j, "BSU", ds_list.GetColumn(i, "BSU"));
			ds_list1.SetColumn(j, "BSU_NM", ds_list.GetColumn(i, "BSU_NM"));
			ds_list1.SetColumn(j, "BPM", ds_list.GetColumn(i, "BPM"));
			ds_list1.SetColumn(j, "AUART", ds_list.GetColumn(i, "AUART"));
			ds_list1.SetColumn(j, "SPART", ds_list.GetColumn(i, "SPART"));
			ds_list1.SetColumn(j, "BSTKD", ds_list.GetColumn(i, "BSTKD"));
			ds_list1.SetColumn(j, "BSTDK", substr(ds_list.GetColumn(i, "BSTDK"),0,4) 
			                               + substr(ds_list.GetColumn(i, "BSTDK"),5,2)
			                               + substr(ds_list.GetColumn(i, "BSTDK"),8,2));
//			ds_list1.SetColumn(j, "BSTDK", ds_list.GetColumn(i, "BSTDK"));
			ds_list1.SetColumn(j, "NETWR", ds_list.GetColumn(i, "NETWR"));
			ds_list1.SetColumn(j, "BILL", ds_list.GetColumn(i, "BILL"));
			ds_list1.SetColumn(j, "CBILL_C", ds_list.GetColumn(i, "CBILL_C"));
			ds_list1.SetColumn(j, "CBILL_D", replace(ds_list.GetColumn(i, "CBILL_D"),"-",""));
			ds_list1.SetColumn(j, "DETAIL", ds_list.GetColumn(i, "DETAIL"));
			ds_list1.SetColumn(j, "CBILL", ds_list.GetColumn(i, "CBILL"));
			ds_list1.SetColumn(j, "CBILL1", ds_list.GetColumn(i, "CBILL1"));
			ds_list1.SetColumn(j, "CBILL2", ds_list.GetColumn(i, "CBILL2"));
			ds_list1.SetColumn(j, "CBILL3", ds_list.GetColumn(i, "CBILL3"));
			ds_list1.SetColumn(j, "CBILL4", ds_list.GetColumn(i, "CBILL4"));
			ds_list1.SetColumn(j, "YBILL1", ds_list.GetColumn(i, "YBILL1"));
			ds_list1.SetColumn(j, "YBILL2", ds_list.GetColumn(i, "YBILL2"));
			ds_list1.SetColumn(j, "YBILL3", ds_list.GetColumn(i, "YBILL3"));
			ds_list1.SetColumn(j, "WAERK", ds_list.GetColumn(i, "WAERK"));
			ds_list1.SetColumn(j, "AFDAT", replace(ds_list.GetColumn(i, "AFDAT"),"-",""));
			j++;
		}			
	}

	ds_list.FireEvent = true;

	tit_clearActionInfo();

	tit_addSaveActionInfo(
		""
		, "cs34:CS3405001A_U1"
		, ""
		, "FLAG");

	tit_callService(
        ""
        , ""
        , "ds_list1=ds_list1:A"
        , ""
        , ""
        , "fn_afterSave");
}

/********************************************************************************
* 마감 저장
********************************************************************************/
function btn_save_OnClick(obj) {
	gfn_clearMsgBar();

	if(ds_list.rowCount() < 1) {
		// 저장할 정보 없음.
		gfn_showMsg("CW00010");
		return;
	}

	for(var i=0; i<ds_list.RowCount(); i++) {
	    if(length(trim(ds_list.GetColumn(i, "CBILL_C"))) == 0) {
				gfn_showAlert(i + 1 + "번째 프로젝트의 사유를 입력하세요.");
				return false;
		}

		if(trim(ds_list.GetColumn(i, "CBILL_C")) < 20) { //미사용코드로 저장하려고 할때 경고 메세지
				gfn_showAlert(i + 1 + "번째 프로젝트의 사유는 유효하지 않습니다.");
				return false;
		}
	}

	ds_list1.ClearData();

	ds_list.Sort("VBELN");

	var j = 0;
	var t_pspid = "";
	var a_pspid = "";
	var s_cbill = "";
	var s_cbill1 = "";
	var s_cbill2 = "";
	var s_cbill3 = "";
	var s_cbill4 = "";

	for(var i=0; i<ds_list.RowCount(); i++) {

		if(i==0) {
			t_pspid = ds_list.GetColumn(i, "PSPID");
			a_pspid = ds_list.GetColumn(i, "PSPID");
			s_cbill  = 0;
			s_cbill1 = 0;
			s_cbill2 = 0;
			s_cbill3 = 0;
			s_cbill4 = 0;
			ds_list1.AddRow();
			ds_list1.SetColumn(j, "FLAG", "U");
		} else {
			a_pspid = ds_list.GetColumn(i, "PSPID");
		}

		if(t_pspid == a_pspid) {

			ds_list1.SetColumn(j, "YYMM", substr(ds_cond.GetColumn(0, "GIJUN"),0,6));
			ds_list1.SetColumn(j, "VBELN", ds_list.GetColumn(i, "VBELN"));
			ds_list1.SetColumn(j, "PSPID", ds_list.GetColumn(i, "PSPID"));
			ds_list1.SetColumn(j, "CBILL_C", ds_list.GetColumn(i, "CBILL_C"));
			ds_list1.SetColumn(j, "CBILL_D", ds_list.GetColumn(i, "CBILL_D"));
			ds_list1.SetColumn(j, "DETAIL", ds_list.GetColumn(i, "DETAIL"));
			ds_list1.SetColumn(j, "WAERK", ds_list.GetColumn(i, "WAERK"));

			s_cbill  = s_cbill  + parseInt(ds_list.GetColumn(i, "CBILL"));
			s_cbill1 = s_cbill1 + parseInt(ds_list.GetColumn(i, "CBILL1"));
			s_cbill2 = s_cbill2 + parseInt(ds_list.GetColumn(i, "CBILL2"));
			s_cbill3 = s_cbill3 + parseInt(ds_list.GetColumn(i, "CBILL3"));
			s_cbill4 = s_cbill4 + parseInt(ds_list.GetColumn(i, "CBILL4"));

			ds_list1.SetColumn(j, "CBILL" , s_cbill );
			ds_list1.SetColumn(j, "CBILL1", s_cbill1);
			ds_list1.SetColumn(j, "CBILL2", s_cbill2);
			ds_list1.SetColumn(j, "CBILL3", s_cbill3);
			ds_list1.SetColumn(j, "CBILL4", s_cbill4);

			t_pspid = ds_list.GetColumn(i, "PSPID");

		} else {
			s_cbill = 0;
			s_cbill1 = 0;
			s_cbill2 = 0;
			s_cbill3 = 0;
			s_cbill4 = 0;
			j++;
			ds_list1.AddRow();
			ds_list1.SetColumn(j, "FLAG", "U");
			ds_list1.SetColumn(j, "YYMM", substr(ds_cond.GetColumn(0, "GIJUN"),0,6));
			ds_list1.SetColumn(j, "VBELN", ds_list.GetColumn(i, "VBELN"));
			ds_list1.SetColumn(j, "PSPID", ds_list.GetColumn(i, "PSPID"));
			ds_list1.SetColumn(j, "CBILL_C", ds_list.GetColumn(i, "CBILL_C"));
			ds_list1.SetColumn(j, "CBILL_D", ds_list.GetColumn(i, "CBILL_D"));
			ds_list1.SetColumn(j, "DETAIL", ds_list.GetColumn(i, "DETAIL"));
			ds_list1.SetColumn(j, "WAERK", ds_list.GetColumn(i, "WAERK"));

			s_cbill  = s_cbill  + parseInt(ds_list.GetColumn(i, "CBILL"));
			s_cbill1 = s_cbill1 + parseInt(ds_list.GetColumn(i, "CBILL1"));
			s_cbill2 = s_cbill2 + parseInt(ds_list.GetColumn(i, "CBILL2"));
			s_cbill3 = s_cbill3 + parseInt(ds_list.GetColumn(i, "CBILL3"));
			s_cbill4 = s_cbill4 + parseInt(ds_list.GetColumn(i, "CBILL4"));

			ds_list1.SetColumn(j, "CBILL" , s_cbill );
			ds_list1.SetColumn(j, "CBILL1", s_cbill1);
			ds_list1.SetColumn(j, "CBILL2", s_cbill2);
			ds_list1.SetColumn(j, "CBILL3", s_cbill3);
			ds_list1.SetColumn(j, "CBILL4", s_cbill4);

			t_pspid = ds_list.GetColumn(i, "PSPID");

		}	
	}


	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "cs34:CS3405001A_U2"
		, ""
		, "FLAG");

	tit_addSaveActionInfo(
		""
		, "cs34:CS3405001A_U3"
		, ""
		, "FLAG");

	tit_callService(
        ""
        , ""
        , "ds_list1=ds_list1:A"
        , ""
        , ""
        , "fn_afterSave1");

}
