/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	//	gfn_sortGrid(obj,nCell);
	if(nCell == 0) {
		// 전체 선택
		gfn_selectAllGrid2(obj,0,0,true);
		//calc_Amt();
	} else {
		// 그리드 정렬 처리
		gfn_sortGrid(obj,nCell);
	}
}

function btn_upn_OnClick(obj)
{
	var arg = "upn="+quote(ed_upn.Text);
	gfn_openPopup("CS9601001A",true,arg);
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if(strColumnID == "CBILL_C") {
		ds_list.FireEvent = false;
		ds_list.SetColumn(nRow, "CHK", "1");

		ds_list.FireEvent = true;
	}
}

function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);

	var nX = ClientToScreenX(obj,rect[0]);
	var nY = ClientToScreenY(obj,rect[1] + 20);

	if(nCell == 17) { //청구계획일
		PopupDiv0.TrackPopup(nX,nY);
		PopupDiv0.cal_temp.value = ds_list.getColumn(nRow, "CBILL_D");
	}

	PopupDiv0.cal_temp.SetFocus();
}

function PopupDiv0_cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	if(grd_list.GetClickedCell() == 17) {
		ds_list.SetColumn(ds_list.row, "CBILL_D", strText);
	}

	PopupDiv0.ClosePopup();
}
