function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	cal_fdt.Value = substr(gfn_getCurrDate(),0,6) + "01";
	cal_tdt.Value = gfn_getCurrDate();

	cb_seq.Index=0;
	// 협력사 작업 불가  권한 제한
	if(gfn_getUserGroup() > "10"){
		btn_trans.Enable = true;
	} else {
		btn_trans.Enable = false;
	}
}

