/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	gfn_clearMsgBar();

	var user_grp = gfn_getUserGroup();

	if(user_grp == '40') {
		if(gfn_isNull(div_code.cb_big, true, st_bsu.value))
			return;
	}

	var qst = gfn_deptQuery();

	ds_cond.SetColumn(0, "PJT", ed_pjt.Value);
	ds_cond.SetColumn(0, "DNO", med_dno.Value);
	ds_cond.SetColumn(0, "TDT", cal_tdt.Value);
	ds_cond.SetColumn(0, "FDT", cal_fdt.Value);

	var qst = gfn_deptQuery();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs34:CS3406001A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , "WQST=" + quote(qst)
        , ""
        , "fn_afterQuery"
        , true);

}

