/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		// 전체 선택
		gfn_selectAllGrid(obj,0,0,true);
	} else {
		// 그리드 정렬 처리
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 내용증명번호 저장 CS403_CTF
********************************************************************************/
function fn_save() {

	gfn_clearMsgBar();

    if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return;
	}

	ds_list2.ClearData();

	var j = 0;

	for(var i=0; i<ds_list.RowCount(); i++) {	

		if(ds_list.GetColumn(i, "CHK") == true) {	

			ds_list2.AddRow();
			ds_list2.SetColumn(j, "FLAG", "U");
			ds_list2.SetColumn(j, "CS403_DNO", ds_list.GetColumn(i, "CS403_DNO"));
			ds_list2.SetColumn(j, "CS403_UPN", ds_list.GetColumn(i, "CS403_UPN"));
			ds_list2.SetColumn(j, "CS403_GNO", ds_list.GetColumn(i, "CS403_GNO"));
			ds_list2.SetColumn(j, "CS403_SEQ", ds_list.GetColumn(i, "CS403_SEQ"));
			ds_list2.SetColumn(j, "CS403_CTF", ds_list.GetColumn(i, "CS403_CTF"));
			ds_list2.SetColumn(j, "KUNNR", ds_list.GetColumn(i, "KUNNR"));

			j++;
		}
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "cs34:CS3406001A_U2"
		, ""
		, "FLAG");
	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, true);

}

/********************************************************************************
* 기      능   : fn_afterSave
********************************************************************************/
function fn_afterSave(errCode, errMsg) {

	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

