/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query4(nRow) {

	ds_cond2.SetColumn(0, "POSID",    quote(substr(ds_list.GetColumn(nRow,"CS403_GNO"),0,6)));
	ds_cond2.SetColumn(0, "VKGRP",    vkgrp);
	ds_cond2.SetColumn(0, "KUNNR",    kunnr);
	ds_cond2.SetColumn(0, "CST",      cst);
	ds_cond2.SetColumn(0, "PS_PSPID", ps_pspid);
	ds_cond2.SetColumn(0, "SEQ",      seq);
	ds_cond2.SetColumn(0, "DNO",      dno);

	if(substr(gbn,0,1) == "U") {
		ds_cond2.SetColumn(0, "GBN", "ZR11");
	} else if(substr(gbn,0,2) == "NB"){
		ds_cond2.SetColumn(0, "GBN", "ZR12");
	} else if(substr(gbn,0,2) == "NS"){
		ds_cond2.SetColumn(0, "GBN", "ZR13");	
	}

	if(dno > "") {

		fn_query3();// 미수공문 발송현황 ( CS3406001A ) 에서 조회시

	} else {

		tit_clearActionInfo();

		tit_addSearchActionInfo("cs34:CS3403001B_S4"); // 청구번호 비교
		tit_addSearchActionInfo("cs34:CS3403001B_S5"); 

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond2"
			, "ds_dno=ds_dno ds_dno_chk=ds_dno_chk"
			, ""
			, "fn_query2"
			, true
			, true);
	}
}

function fn_query2() {

	tit_clearActionInfo();

	if( ds_dno.RowCount() < 2 ) {

		if(length(ds_dno.GetColumn(0, "GNO")) == 0 && seq == "01") { // 1차 신규 작성

		} else if(length(ds_dno.GetColumn(0, "GNO")) == 0 && seq <> "01") {

			alert(seq + "차 공문을 작성할 수 없습니다.");
			close();
			return;

		} else if(length(ds_dno.GetColumn(0, "DNO")) > 0 ) { // 현재 차수 내용 불러오기

			tit_addSearchActionInfo("cs34:CS3403001B_S2");

		} else if(length(ds_dno.GetColumn(0, "DNO")) == 0 ) { // 앞차수 내용 불러오기

			if (seq == "03" && length(ds_dno.GetColumn(0, "DNO_02")) == 0 ) {

				alert(seq + "차 공문을 작성할 수 없습니다.");
				close();
				return;

			} else{

				tit_addSearchActionInfo("cs34:CS3403001B_S3");
			}
		} 

	} else { 
		alert(seq + "차 공문을 작성할 수 없습니다.");
		close();
		return;
	}

	tit_addSearchActionInfo("cs34:CS3403001B_S1"); // 계좌정보 조회

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond2"
		, "ds_list4=ds_list2 ds_bank=ds_bank"
		, ""
		, "fn_afterPrint"
		, true
		, true);	

}

function fn_query3() {

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs34:CS3403001B_S6"); 
	tit_addSearchActionInfo("cs34:CS3403001B_S1"); // 계좌정보 조회

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond2"
		, "ds_list4=ds_list2 ds_bank=ds_bank"
		, ""
		, "fn_afterPrint"
		, true
		, true);	

}
/********************************************************************************
* 기      능   : print 40 출력
********************************************************************************/
function fn_print() {	

	if (cb_seq.Index == 0) {
		var arr = [parseInt(i) + 1];
		gfn_showMsg("공문차수를 선택하시고 출력하시기 바랍니다.",arr);
		return;
	}

	//============================================================================
	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showAlert("CW00013");		//처리할 자료를 먼저 선택해 주십시오.
		return;
	}
	//============================================================================

	gfn_copyDs(ds_list, ds_list3, "CHK=1");

	var q_str="";

	for(iCnt=0 ; iCnt < ds_list3.rowcount ; iCnt++) {
		var tmp = ds_list3.GetColumn(iCnt, "CS403_DNO");

		if (iCnt == 0 ) {
			q_str = "'" + tmp + "'" ;
		} else { 
			q_str = q_str + ",'"+tmp+"' ";
		}
	} 

	tit_clearActionInfo();

	var tmp = ds_list3.GetColumn(0, "CS403_SEQ");

	if (tmp == "01") {
		tit_addSearchActionInfo("cs34:CS3406001A_S2"); 
	} else if (tmp == "02") {
		tit_addSearchActionInfo("cs34:CS3406001A_S3"); 
	} else if (tmp == "03") {
		tit_addSearchActionInfo("cs34:CS3406001A_S4"); 
	}

	ds_list3.ClearData();

	tit_callService(
		""
		, ""
		, ""
		, "ds_list3=ds_list3"
		, "DNOL=" + quote(q_str) 
		, "fn_printQuery"
		, ""
		, true);

}

/********************************************************************************
* 기      능   : 인쇄여부 'Y' 변경처리 
********************************************************************************/
function fn_printQuery() {

	if(ds_list3.rowcount < 1) {
		gfn_showAlert("미수공문 내역이 존재하지 않습니다. 출력처리를 종료합니다.");
		return;
	}

	var q_sta = "";

	for(iCnt=0 ; iCnt < ds_list3.rowcount ; iCnt++) { 

		var tmp = ds_list3.GetColumn(iCnt, "DNO");

		if (iCnt == 0 ) {
			q_sta = "'" + tmp + "'" ;
		} else { 
			q_sta = q_sta + ",'"+tmp+"' ";
		}
	} 	

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs34:CS3406001A_U1");
	tit_callService(
        ""
        , ""
        , "ds_list3=ds_list3"
        , ""
        , "DNOL=" + quote(q_sta) 

        , "fn_printPopup"
        , ""
        , true);

}

/********************************************************************************
* 기      능   : 출력 팝업
********************************************************************************/
function fn_printPopup() {
	//UbiReport Call 

	var tmp = ds_list3.GetColumn(0, "SEQ");

	for (i =0 ; i < ds_list3.rowcount ; i++) {
		ds_list3.SetColumn( i,"SGUSR",G_USER_NAME);
	}

	if(tmp == "01") {
		Dialog("CS34::CS3403001C.xml", "");
	} else if(tmp == "02"){
		Dialog("CS34::CS3403002C.xml", "");
	} else if(tmp == "03"){
		Dialog("CS34::CS3403003C.xml", "");
	}
	cb_seq.Index = 0;
}

/********************************************************************************
* 기      능   : DS_LIST  필터 조건 (1차,2차,3차, 전체)
********************************************************************************/
function cb_seq_OnCharChanged(obj,strPreText,strPostText) {
	ufn_clearCheck(); 

	if (cb_seq.Index==0 ) {
		ds_list.UnFilter();
	} else if (cb_seq.Index==1 ) {
		ds_list.Filter("CS403_SEQ=='01'");
	} else if (cb_seq.Index==2 ) {
		ds_list.Filter("CS403_SEQ=='02'");
	} else if (cb_seq.Index==3 ) {
		ds_list.Filter("CS403_SEQ=='03'");
	}
}

/********************************************************************************
* 기      능   : 체크박스에 항목을 차수 변경시 제어하는 함수
********************************************************************************/
function ufn_clearCheck() {

	//전체가 아닌 차수가 변경되면 모든 선택을 클리어시킴
	if (cb_seq.Index != 0) {
		for (i=0; i < ds_list.rowcount ; i++) {
			ds_list.SetColumn(i,"CHK",0);
		}
	}

	//전체 선택 자체를 클리어시킴
	var strColID = grd_list.GetCellProp("head", 0, "Text");

	if ( length( strColID ) == 1 ) {
		grd_list.SetCellProp("head", 0, "Text", "0");
	}

	//grd_list.Redraw = true;

	return;
}

function btn_trans_OnClick(obj)
{
	 // if(G_USER_ID != "2021200"){
		 // alert("전자우편 양식 변경 중입니다. 서비스 판매관리팀에 문의 바랍니다.");
		 // return;
	 // }

	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showAlert("CW00013");		//처리할 자료를 먼저 선택해 주십시오.
		return;
	}

	// 중복실행 방지 2020.11.12. Han J.H
	if(executeChk == true) {
		return;
	}		

	ds_list_copy.ClearData();
	var j = 0;
	var fcnt = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) {

			if(length(trim(ds_list.GetColumn(i, "CON_KEY"))) != 0 ){
				gfn_showAlert("공문번호 : "+ds_list.GetColumn(i, "CS403_DNO") +" 는 이미 우편 발송이 완료되었습니다.");
				return false;	
			}
			// 전자우편 필수 항목 체크
			if(length(trim(ds_list.GetColumn(i, "S004"))) == 0 ){
				gfn_showAlert("공문번호 : "+ds_list.GetColumn(i, "CS403_DNO") +" 의 발송구분 정보가 누락되었습니다.");
				return false;	
			}
			if(length(trim(ds_list.GetColumn(i, "RECEV_NM"))) == 0 ){
				gfn_showAlert("공문번호 : "+ds_list.GetColumn(i, "CS403_DNO") +" 의 수신인명 정보가 누락되었습니다.");
				return false;	
			}
			var zip = length(trim(ds_list.GetColumn(i, "NZIB")));
			var adr1 = length(trim(ds_list.GetColumn(i, "ADR1")));

			if( zip == 0 or adr1 == 0 ) {
				gfn_showAlert("공문번호 : "+ds_list.GetColumn(i, "CS403_DNO") +" 의 수신처 주소 정보가 누락되었습니다.");
				return false;	
			}
			// 3차 공문 필수 항목 체크
			if(trim(ds_list.GetColumn(i, "CS403_SEQ")) == "03" && length(trim(ds_list.GetColumn(i, "DCT"))) == 0 ){
				gfn_showAlert("공문번호 : "+ds_list.GetColumn(i, "CS403_DNO") +" 의 저장 작업을 다시 해주세요.");
				return false;	
			}
			ds_list_copy.AddRow();
			ds_list_copy.SetColumn(j,"DNO" , ds_list.GetColumn(i,"CS403_DNO"));
			ds_list_copy.SetColumn(j,"SEQ" , ds_list.GetColumn(i,"CS403_SEQ"));
			ds_list_copy.SetColumn(j,"UPN" , ds_list.GetColumn(i,"CS403_UPN"));
			ds_list_copy.SetColumn(j,"S004", ds_list.GetColumn(i,"S004"));
			ds_list_copy.SetColumn(j,"SEAL", ds_list.GetColumn(i,"SEAL"));
			j++;
		}	
	}	

	if(!gfn_showConfirm("전자우편 서비스 신청을 하시겠습니까?          " + "총 " +  j + " 건")) return;

	executeChk = true;

	tit_callService(
        "CS4201001A_ACT"
        , "insertHeader"
        , "ds_list=ds_list_copy"
        , "ds_list=ds_list ds_error=ds_error"
        , ""
        , "fn_afterInsert"
        ,""
        ,true
        ,"");		
}

function fn_afterInsert(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showAlert("CE00001");
	} else {
		gfn_showAlert("전자우편 서비스 신청이 완료되었습니다.");
		fn_query();
	}

	executeChk = false;
}
