function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	var codeArr = ["CS170"];
	var dsObjArr = [ds_auart];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-4,10);
	cb_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_montht.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_yearf.SetFocus();


}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	gfn_showMsg("CI00007");

	cb_auart.Index = 0;

}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum) {
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--) {
		ds_yearf_cd.AddRow();
		ds_yearf_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yearf_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		ds_yeart_cd.AddRow();
		ds_yeart_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yeart_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}

	cb_yearf.index = eNum;
	cb_yeart.index = eNum;
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	gfn_clearMsgBar();
/*	2015.02.16 YAR 제품군 전체 조회가능도록 수정
	if(length(cb_pbn.value) == 0) {
		var arr = [st_pbn.value];
		gfn_showMsg("CW00001", arr);
		return;
	}
*/
	var user_grp = gfn_getUserGroup();

	chk_sj.Value = false;
	chk_lc.Value = false;

	ds_cond.SetColumn(0, "SMON", cb_yearf.value + cb_monthf.Value + "");
	ds_cond.SetColumn(0, "EMON", cb_yeart.value + cb_montht.Value + "");
	ds_cond.SetColumn(0, "PROJ", ed_upn.value);

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "LIFNR", G_LGORT);
		ds_cond.SetColumn(0, "ZMAN", '');
		ds_cond.SetColumn(0, "VKGRP", '');
		ds_cond.SetColumn(0, "REGION", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "LIFNR", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "ZMAN", G_USER_ID);
		ds_cond.SetColumn(0, "VKGRP", '');
		ds_cond.SetColumn(0, "REGION", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "LIFNR", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "ZMAN", '');
		ds_cond.SetColumn(0, "VKGRP", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "REGION", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "LIFNR", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "ZMAN", '');
			ds_cond.SetColumn(0, "VKGRP", '');
			ds_cond.SetColumn(0, "REGION", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "ZMAN", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "VKGRP", '');
			ds_cond.SetColumn(0, "REGION", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "ZMAN", '');
			ds_cond.SetColumn(0, "VKGRP", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "REGION", '');
		}
	}

	if(length(ds_cond.GetColumn(0,"LIFNR")) < 1)
	{
		gfn_showMsg("CW00001", st_bsu.Text);
	}


	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();
	tit_callService(
        "CS3601001A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list=ds_list ds_error=ds_error"
        , ""
        , "fn_afterQuery");
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

/********************************************************************************
* 기      능   : 액셀  
