function chk_all_OnClick(obj,strValue)
{
	//var mod = '';
	//var  sj = '';
	//var  lc = '';

	var mod = '' ,  sj = '' ,  lc = '';


	if(chk_mod.Value == true) 	mod = "1";
	if(chk_sj.Value == true)     sj = "1";
	if(chk_lc.Value == true) 	 lc = "1";


	if(mod == '' && sj == '' && lc == '')	ds_list.UnFilter();
	if(mod == '' && sj == '' && lc != '')	ds_list.Filter("ILSIC_YN == 'Y'");	
	if(mod == '' && sj != '' && lc == '')	ds_list.Filter("KUNNRT != '현대엘리베이터서비스 주식회사'");	
	if(mod == '' && sj != '' && lc != '')	ds_list.Filter("KUNNRT != '현대엘리베이터서비스 주식회사' && ILSIC_YN == 'Y'" );	

	if(mod != '' && sj == '' && lc == '')	ds_list.Filter("SUBSTR(ZZPJT_ID,6,2) == 'NC'");	
	if(mod != '' && sj == '' && lc != '')   ds_list.Filter("SUBSTR(ZZPJT_ID,6,2) == 'NC' && ILSIC_YN == 'Y'" );	
	if(mod != '' && sj != '' && lc == '')   ds_list.Filter("SUBSTR(ZZPJT_ID,6,2) == 'NC' && KUNNRT != '현대엘리베이터서비스 주식회사'" );	
	if(mod != '' && sj != '' && lc != '')	ds_list.Filter("SUBSTR(ZZPJT_ID,6,2) == 'NC' && KUNNRT != '현대엘리베이터서비스 주식회사' && ILSIC_YN == 'Y'" );	


}
