/********************************************************************************
* 기      능   : 전송 Callback  
********************************************************************************/
function fn_afterTrans(errCode, errMsg) {
	if(errCode == 0)
		fn_query();
}

/********************************************************************************
* 기      능   : 승인 Callback  
********************************************************************************/
function fn_afterApproval(errCode, errMsg) {
	if(errCode == 0)
		fn_query();
}

/********************************************************************************
* 기      능   : 취소 Callback  
********************************************************************************/
function fn_afterCancel(errCode, errMsg) {
	if(errCode == 0)
		fn_query();
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_gnd.index = 0;
	cb_pst.index = 0;
	cb_jpgbn.index = 0;
	cb_fyn.index = 0;
	ds_pst_cd.filter("CODE = '' || CODE = 'A1' || CODE = 'A5' || CODE = 'A6'");
	ds_gnd_cd.filter("CODE <> 'E'");

/*** 마감작업시 임시 사용 ***/
//	cb_year.Value = "2014";
//	cb_month.Value = "01";
//	cb_year.Enable = false;
//	cb_month.Enable = false;
/****************************/

	if(G_VEND_CODE == "0000009916") { //서비스사업부
		cb_jpgbn.Value = "1";
		cb_jpgbn.Enable = false;
	} else if(G_VEND_CODE == "0000009935" || G_VEND_CODE == "0000009039") { //주차사업부
		cb_jpgbn.Value = "2";
		cb_jpgbn.Enable = false;
	}
	ds_pst_grd.Copy(ds_pst_cd);
	ds_gnd_grd.Copy(ds_gnd_cd);
	ds_pst_grd.DeleteRow(0);
	ds_gnd_grd.DeleteRow(0);

}
