	close();
}
/********************************************************************************
* 기      능   : 전송  
********************************************************************************/
function fn_trans() {
	if(ds_list.rowcount == 0) {
		gfn_showMsg("CW00010");
		return;
	}

	if(ds_list.getcolumn(0,"PST") != "A1") {
		var arr = ["작성", "발송"];
		gfn_showMsg("CW00068", arr);	
		return;
	}

	ds_list2.ClearData();

	var t_cnt = 0;
	for(var i=0; i<ds_list.rowcount; i++) {
//		ds_list.SetColumn(i,"PST","A5");
		ds_list.SetColumn(i,"GND",ds_cond_bak.GetColumn(0,"GND"));
		ds_list.SetColumn(i,"ARA",ds_cond_bak.GetColumn(0,"ARA"));
		ds_list.SetColumn(i,"PST","A6"); // 2010.05.03 LJH 협력사 기성 전송시 접수에서 승인 상태로 변경
		ds_list.SetColumn(i,"GBN","B");

		if(trim(ds_list.GetColumn(i,"PJT")) > "") {
			var rows = ds_list2.AddRow();
			ds_list2.CopyRow(rows, "ds_list", i);

			if(t_cnt == 0) {
				ds_data.CopyRow(0, "ds_list", i);
				t_cnt++;
			}
		}
	}
/*
	tit_clearActionInfo();
	tit_addMultiActionInfo("cs41:CS4101001B_U1");

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list2:A"
        , ""
        , ""
        , "fn_afterTrans");

*/
	var t_sql = "";

	if(trim(ds_cond_bak.GetColumn(0, "GND")) > "") {
		if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
			t_sql = "cs41:CS4101001B_S6";
		} else {
			t_sql = "cs41:CS4101001B_S8";
		}
	} else {
		if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
			t_sql = "cs41:CS4101001B_S7";
		} else {
			t_sql = "cs41:CS4101001B_S9";
		}
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo(t_sql);
	tit_callService(
		""
		, ""
		, "ds_data=ds_data"
		, "ds_chkResult=ds_chkResult"
		, ""
		, "ufn_chkClose"
		, true);
}

