	if(ds_list.rowcount == 0) {
		gfn_showMsg("CW00010");
		return;
	}
/*
	if ( div_code.cb_mid.value == 'E5' && ds_list.getcolumn(0,"PST") != "A1" )
	{
		var arr = ["작성", "승인"];
		gfn_showMsg("CW00068", arr);	
		return;
	}
	else if ( div_code.cb_mid.value != 'E5' && ds_list.getcolumn(0,"PST") != "A5" ) 
	{
		var arr = ["접수", "승인"];
		gfn_showMsg("CW00068", arr);	
		return;
	}
*/
	// 2010.05.03 LJH 협력사 기성 전송시 접수에서 승인 상태로 변경
	if(ds_list.getcolumn(0,"PST") != "A1") {
		var arr = ["작성", "승인"];
		gfn_showMsg("CW00068", arr);	
		return;
	}

	ds_list2.ClearData();

	var t_cnt = 0;
	for(var i=0; i<ds_list.rowcount; i++) {
		ds_list.SetColumn(i,"GND",ds_cond_bak.GetColumn(0,"GND"));
		ds_list.SetColumn(i,"ARA",ds_cond_bak.GetColumn(0,"ARA"));
		ds_list.SetColumn(i,"PST","A6");
		ds_list.SetColumn(i,"GBN","B");

		if(trim(ds_list.GetColumn(i,"PJT")) > "") {
			var rows = ds_list2.AddRow();
			ds_list2.CopyRow(rows, "ds_list", i);

			if(t_cnt == 0) {
				ds_data.CopyRow(0, "ds_list", i);
				t_cnt++;
			}
		}
	}

	var t_sql = "";

	if(trim(ds_cond_bak.GetColumn(0, "GND")) > "") {
		if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
			t_sql = "cs41:CS4101001B_S6";
		} else {
			t_sql = "cs41:CS4101001B_S8";
		}
	} else {
		if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
			t_sql = "cs41:CS4101001B_S7";
		} else {
			t_sql = "cs41:CS4101001B_S9";
		}
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo(t_sql);
	tit_callService(
		""
		, ""
		, "ds_data=ds_data"
		, "ds_chkResult=ds_chkResult"
		, ""
		, "ufn_chkClose"
		, true);
}

function ufn_chkClose() {	
	if(ds_chkResult.GetColumn(0, "CNT") > 0) {
		gfn_showAlert("CW00172");
	} else {

		var t_sql = "";
		var t_sql2 = "";

		if(trim(ds_cond_bak.GetColumn(0, "GND")) > "") {
			if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
				t_sql = "cs41:CS4101001B_I1";
			} else {
				t_sql = "cs41:CS4101001B_I4";
			}
		} else {
			if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
				t_sql = "cs41:CS4101001B_I3";
			} else {
				t_sql = "cs41:CS4101001B_I5";
			}
		}

		if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
			t_sql2 = "cs41:CS4101001B_U1";
		} else {
			t_sql2 = "cs41:CS4101001B_U3";
		}

		tit_clearActionInfo();
		tit_addMultiActionInfo(t_sql2);
		tit_addSingleActionInfo(t_sql);
		tit_addSingleActionInfo("cs41:CS4101001B_U2"); //20080911 LJH 해당지급년월 직영 기성비 진행상태를 A1 ==> A6로 변경작업
//		tit_addSingleActionInfo("cs41:CS4101001B_I7"); //마감테이블(ZCST166)에 직영마감 생성작업

		tit_callService(
			""
			, ""
			, "ds_list=ds_list2:A"
			, ""
			, ""
			, "fn_afterApproval");	
	}
}

/********************************************************************************
* 기      능   : 취소
********************************************************************************/
function fn_cancel() {
	if(ds_list.rowcount == 0) {
		gfn_showMsg("CW00010");
		return;
	}

	ds_list2.ClearData(); // 데이터셋 클리어

	if(user_grp == '30') {
		if(ds_list.getcolumn(0,"PST") != "A5") {
			var arr = ["접수", "취소"];
			gfn_showMsg("CW00068", arr);	
			return;
		}

		if(ds_check.getcolumn(0,"CNT") != "0") {
			gfn_showMsg("CW00069");	
			return;
		}
		//=============================================기존소스 백업 시작===================================================================
		/*for(var i=0; i<ds_list.rowcount; i++) {
			ds_list.SetColumn(i,"PST","A1");
			ds_list.SetColumn(i,"GBN","D");

			if(trim(ds_list.GetColumn(i,"PJT")) > "") {
				var rows = ds_list2.AddRow();
				ds_list2.CopyRow(rows, "ds_list", ds_list.row);
			}
		}*/
		//=============================================기존소스 백업 종료===================================================================

		//=============================================부분취소 데이터셋 세팅부분 로직 분기 시작============================================
			ds_list.SetColumn(ds_list.currow,"PST","A1");
			ds_list.SetColumn(ds_list.currow,"GBN","D");

			if(trim(ds_list.GetColumn(i,"PJT")) > "") {
				var rows = ds_list2.AddRow();
				ds_list2.CopyRow(rows, "ds_list", ds_list.currow); // 한건씩 삭제
			} else {
				// 한건씩 취소시 소계 부분을 선택했다면 취소처리 되지 않도록 처리
				gfn_showAlert("소계 부분을 선택하셨습니다. 해당 프로젝트 행을 선택 후 다시 시도해주시기 바랍니다.");
				return;			
			}
		//=============================================부분취소 데이터셋 세팅부분 로직 분기 종료============================================

		var t_sql = "";

		if(trim(ds_cond_bak.GetColumn(0, "GND")) > "") {
			if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
				t_sql = "cs41:CS4101001B_D1";
			} else {
				t_sql = "cs41:CS4101001B_D4";
			}
		} else {
			if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
				t_sql = "cs41:CS4101001B_D3";
			} else {
				t_sql = "cs41:CS4101001B_D5";
			}
		}

		var t_sql2 = "";

		if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
			t_sql2 = "cs41:CS4101001B_U1";
		} else {
			t_sql2 = "cs41:CS4101001B_U3";
		}

		tit_clearActionInfo();
		tit_addMultiActionInfo(t_sql2);
		tit_addSingleActionInfo(t_sql);

		tit_callService(
			""
			, ""
			, "ds_list=ds_list2:A"
			, ""
			, ""
			, "fn_afterCancel");
	}

	if(user_grp == '40') {
		if(ds_list.getcolumn(0,"PST") != "A6") {
			var arr = ["완료", "취소"];
			gfn_showMsg("CW00068", arr);	
			return;
		}

		if(ds_check.getcolumn(0,"CNT") != "0") {
			gfn_showMsg("CW00070");	
			return;
		}

		//========================================전체취소 및 부분취소를 위한 데이터셋 세팅부분 로직 분기 시작===========================================
		// 2019.03.08 Han J.H
		if(chk_cancle.Value == '1') {
			if(!gfn_showConfirm("전체 기성비지급예정 내역을 삭제하시겠습니까?"))
			{
				gfn_showAlert("CW10025", "취소처리가 취소되었습니다.");
				return;
			}

			for(var i=0; i<ds_list.rowcount; i++) {
				ds_list.SetColumn(i,"GND",ds_cond_bak.GetColumn(0,"GND"));
				ds_list.SetColumn(i,"ARA",ds_cond_bak.GetColumn(0,"ARA"));

				if(div_code.cb_mid.value == 'E5') {
					ds_list.SetColumn(i,"PST","A1");
					ds_list.SetColumn(i,"GBN","D");	
				} else {
					ds_list.SetColumn(i,"PST","A1");
					ds_list.SetColumn(i,"GBN","D");	
				}

				if(trim(ds_list.GetColumn(i,"PJT")) > "") {
					var rows = ds_list2.AddRow();
					//==================기존소스 백업==============================
					//ds_list2.CopyRow(rows, "ds_list", ds_list.row);
					//=============================================================
					ds_list2.CopyRow(rows, "ds_list", i); // 전체 삭제되도록 변경.
				}
			}		
		} else if(chk_cancle.Value == '0') {
			if( !gfn_showConfirm("CI00013", "호기 : "+ quote(ds_list.GetColumn(ds_list.currow,"HOGI"))) )
			{
				gfn_showAlert("CW10025", "취소처리가 취소되었습니다.");
				return;
			}			

			ds_list.SetColumn(ds_list.currow,"GND",ds_cond_bak.GetColumn(0,"GND"));
			ds_list.SetColumn(ds_list.currow,"ARA",ds_cond_bak.GetColumn(0,"ARA"));

			ds_list.SetColumn(ds_list.currow,"PST","A1");
			ds_list.SetColumn(ds_list.currow,"GBN","D");

			if(trim(ds_list.GetColumn(ds_list.currow,"PJT")) > "") {
				var rows = ds_list2.AddRow();
				ds_list2.CopyRow(rows, "ds_list", ds_list.currow); // 한건씩 삭제
			} else {
				// 한건씩 취소시 소계 부분을 선택했다면 취소처리 되지 않도록 처리
				gfn_showAlert("소계 부분을 선택하셨습니다. 해당 프로젝트 행을 선택 후 다시 시도해주시기 바랍니다.");
				return;
			}
		}
		//========================================전체취소 및 부분취소를 위한 데이터셋 세팅부분 로직 분기 종료===========================================

		var t_sql = "";

		if(trim(ds_cond_bak.GetColumn(0, "GND")) > "") {
			if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
				t_sql = "cs41:CS4101001B_D1";
			} else {
				t_sql = "cs41:CS4101001B_D4";
			}
		} else {
			if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
				t_sql = "cs41:CS4101001B_D3";
			} else {
				t_sql = "cs41:CS4101001B_D5";
			}
		}

		var t_sql2 = "";

		if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
			t_sql2 = "cs41:CS4101001B_U1";
		} else {
			t_sql2 = "cs41:CS4101001B_U3";
		}

		tit_clearActionInfo();
		tit_addMultiActionInfo(t_sql2);
		tit_addSingleActionInfo(t_sql);

		tit_callService(
			""
			, ""
			, "ds_list=ds_list2:A"
			, ""
			, ""
			, "fn_afterCancel");
	}
}

