/********************************************************************************
* 기      능   : 기성 확정 
* 20170531 기성 절차 변경 , 이하림 
* 20171226 주차 데이타 생성 순서 변경, 이하림 
********************************************************************************/
function btn_copy_OnClick(obj) {
	if(ds_list.rowcount == 0) {
		gfn_showMsg("CW00010");
		return;
	}

	ds_list2.ClearData();
	var j=0;

	for(var i=0; i<ds_list.rowcount; i++) {
		if(trim(ds_list.GetColumn(i,"PJT")) > "" && j == 0) {
			var rows = ds_list2.AddRow();
			ds_list2.CopyRow(rows, "ds_list", i);
			j++;
		}
	}

	var t_sql = "";
	var t_sql2 = "";
	var t_sql3 = "";
	var t_sql4 = "";

//-----------------------------------------------------
// 승강기 1 / 주차 2 
// 승강기 - >  임시테이블 zcst034  insert
// 주차   - >  zcst034 데이타 생성 후 zmmt034 insert  

	if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
	//	t_sql = "cs41:CS4101001B_D2"; // ZMMT034 DELETE 
	//	t_sql2 = "cs41:CS4101001B_I2"; // ZMMT034 INSERT
		t_sql3 = "cs41:CS4101001B_D2A"; // ZCST034 DELETE
		t_sql4 = "cs41:CS4101001B_I2A"; // ZCST034 INSERT
	} else {
		t_sql3 = "cs41:CS4101001B_D6A"; // ZCST034 DELETE
		t_sql4 = "cs41:CS4101001B_I6A"; // ZCST034 INSERT
		t_sql  = "cs41:CS4101001B_D6";  // ZMMT034 DELETE 
		t_sql2 = "cs41:CS4101001B_I6B";  // ZMMT034 INSERT
		// t_sql2 = "cs41:CS4101001B_I6";  // ZMMT034 INSERT
	}

	tit_clearActionInfo();

	if(trim(ds_cond_bak.GetColumn(0, "JPGBN")) == "1") {
		// tit_addSingleActionInfo(t_sql);
		// tit_addSingleActionInfo(t_sql2);
		tit_addSingleActionInfo(t_sql3);
		tit_addSingleActionInfo(t_sql4);
	} else {
		tit_addSingleActionInfo(t_sql3);
		tit_addSingleActionInfo(t_sql4);
		tit_addSingleActionInfo(t_sql);
		tit_addSingleActionInfo(t_sql2);
	}

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list2:A"
        , ""
        , ""
        , ""
        , ""
        , ""
        , true);
}

