function btn_sto_OnClick(obj)
{	
	ds_list.ClearData();
	//ds_cond.SetColumn(0,"YYYYMM",ds_cond.GetColumn(0,"YEAR")+ds_cond.GetColumn(0,"MONTH"));
	ds_cond.SetColumn(0,"YYYYMM",ds_cond.GetColumn(0,"YYYYMM"));
	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	if(user_grp == '40') {
		if(div_code.cb_mid.value == 'E5') {
			ds_cond.SetColumn(0,"GUBUN","");
		} else if(div_code.cb_mid.value != 'E5') {
			ds_cond.SetColumn(0,"GUBUN","A");
		}	
	} else {
		ds_cond.SetColumn(0,"GUBUN","");
	}

	if(div_code.cb_mid.value == "") {
		ds_cond.SetColumn(0,"GUBUN","X");
	}

	if(cb_gnd.value == "" || cb_gnd.value == "F") {
		ds_cond.SetColumn(0,"GNDBU","X");
	} else {
		ds_cond.SetColumn(0,"GNDBU","");
	}

	if(ed_upn.value == "") {
		ds_cond.SetColumn(0,"UPN","");
		ds_cond.SetColumn(0,"CST","");
	} else {
		ds_cond.SetColumn(0,"UPN",ed_upn.value);
		ds_cond.SetColumn(0,"CST",ed_cst.value);
	}

	ds_cond.SetColumn(0,"JYN",cb_jyn.Value);	

	tit_callService(
        "CS1299001A_ACT"
        , "queryCS4101"
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery2"
        , true);    


}
function fn_afterQuery2(errCode, errMsg) {	
	ds_cond_bak.copy(ds_cond); // 2019.07.01 STO 조회 콜백시 기존 조회조건 복사하도록 변경. Han J.H
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}
