//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술
	gfn_initForm(true);

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-3,10);
	cb_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_montht.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;

	//코드콤보 설정 : 계약구분, 제품구분
	var codeArr = ["CS135","CS131"];
	var dsObjArr = [ds_gbn1_cd,ds_gbn2_cd];
	var addTypeArr = [G_CODE_ALL,G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	tit_clearActionInfo();
	if( gfn_getUserGroup() == "10" )
	{
		tit_addSearchActionInfo("cs41:CS4102001A_S1");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_lifnr_cd=ds_lifnr_cd"
			, ""
			, "fn_afterQueryC"
			, true);
	}
	else if( gfn_getUserGroup() == "20" || gfn_getUserGroup() == "30" || gfn_getUserGroup() == "40" )
	{
		tit_addSearchActionInfo("cs41:CS4102001A_S2");
		tit_addSearchActionInfo("cs41:CS4102001A_S3");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_area_cd=ds_area_cd ds_lifnr_cd=ds_lifnr_cd"
			, ""
			, "fn_afterQueryC"
			, true);
	}
	cb_yearf.SetFocus();
}

/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_yearf_cd.AddRow();
		ds_yearf_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yearf_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		ds_yeart_cd.AddRow();
		ds_yeart_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yeart_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_yearf.index = eNum;
	cb_yeart.index = eNum;
}

/********************************************************************************
* 기      능   : 지역콤보 변경시 보수협력사 콤보 filtering해줌
********************************************************************************/
function cb_area_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	cb_lifnr.value = "";
	ds_lifnr_cd.filter("BSU_ARA == " + quote( strCode) +" || CODE == ''" );
}
/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	//년도필드 필수체크
	if(gfn_isNull(cb_yearf, true, st_yymm.value))
		return;
	//월필드 필수체크
	if(gfn_isNull(cb_monthf, true, st_yymm.value))
		return;
	if(gfn_isNull(cb_yeart, true, st_yymm.value))
		return;
	if(gfn_isNull(cb_montht, true, st_yymm.value))
		return;
	if(parseInt(ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF")) >
	   parseInt(ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT")))
	{
		gfn_showMsg("CW00009");
		cb_yeart.SetFocus();
		return;
	}
	//지역필드 필수체크(보수협력사 로그인시)
	//if( gfn_getUserGroup() == '10' && gfn_isNull(cb_area, true, st_lifnr.value))
	//	return;
	//보수협력사필드 필수체크(보수협력사 로그인시)
	if( gfn_getUserGroup() == '10' && gfn_isNull(cb_lifnr, true, st_lifnr.value))
		return;
	//지역필드 필수체크(지방사무소 로그인시)
	if( gfn_getUserGroup() == '30' && gfn_isNull(cb_area, true, st_lifnr.value))
		return;

	var fYear  = parseInt(ds_cond.GetColumn(0,"YEARF"));
	var tYear  = parseInt(ds_cond.GetColumn(0,"YEART"));
	var fMonth = parseInt(ds_cond.GetColumn(0,"MONTHF"));
	var tMonth = parseInt(ds_cond.GetColumn(0,"MONTHT"));

	//조회기간: 0=같은달,1=2개월,2=3개월..
	if(((tYear-fYear)*12+tMonth-fMonth) > 1)
	{
		gfn_showMsg("CW00078"," 2 ");
		cb_montht.SetFocus();
		return;
	}

	ds_cond.SetColumn(0,"YYYYMMF",ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF"));
	ds_cond.SetColumn(0,"YYYYMMT",ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT"));

	if(ds_cond.GetColumn(0,"GUBUN2") == "A")
	{
		ds_cond.SetColumn(0,"GBN2","L");
		ds_cond.SetColumn(0,"GBN3","S");
		ds_cond.SetColumn(0,"GBN4","W");
	}
	else if(ds_cond.GetColumn(0,"GUBUN2") == "B")
		ds_cond.SetColumn(0,"GBN2","J");
	else if(ds_cond.GetColumn(0,"GUBUN2") == "C")
		ds_cond.SetColumn(0,"GBN2","M");
	else if(ds_cond.GetColumn(0,"GUBUN2") == "D")
		ds_cond.SetColumn(0,"GBN2","G");

	tit_clearActionInfo();
	if(gfn_getUserGroup() == "10")
		tit_addSearchActionInfo("cs41:CS4102001A_S4");
	else if(gfn_getUserGroup() == "20")
		tit_addSearchActionInfo("cs41:CS4102001A_S5");
	else if(gfn_getUserGroup() == "30")
		tit_addSearchActionInfo("cs41:CS4102001A_S5");
	else if(gfn_getUserGroup() == "40")
		tit_addSearchActionInfo("cs41:CS4102001A_S6");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {
	if( gfn_getUserGroup() == "10" )
	{
		cb_area.Enable = false;
		cb_lifnr.Index = 0;
	}
	else if ( gfn_getUserGroup() == "20" )
	{
		cb_area.Enable = true;
		ds_area_cd.filter("CODE == " + quote( G_AREA_CODE) );
		cb_area.Index = 0;
		// 협력사 전체추가
		gfn_insertEmptyRow( ds_lifnr_cd, G_CODE_ALL );
		cb_lifnr.value = "";
		ds_lifnr_cd.filter("BSU_ARA == " + quote( G_AREA_CODE) +" || CODE == ''");
	}
	else if ( gfn_getUserGroup() == "30" )
	{
		cb_area.Enable = true;
		ds_area_cd.filter("CODE == " + quote( G_AREA_CODE) );
		cb_area.Index = 0;
		// 협력사 전체추가
		gfn_insertEmptyRow( ds_lifnr_cd, G_CODE_ALL );
		cb_lifnr.value = "";
		ds_lifnr_cd.filter("BSU_ARA == " + quote( G_AREA_CODE) +" || CODE == ''");
	}
	else if ( gfn_getUserGroup() == "40" )
	{
		// 지역 전체추가
		gfn_insertEmptyRow( ds_area_cd, G_CODE_ALL );
		cb_area.value = "";
		// 협력사 전체추가
		gfn_insertEmptyRow( ds_lifnr_cd, G_CODE_ALL );
		cb_lifnr.value = "";
	}
}


/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_gbn1.Index = 0;
	cb_gbn2.Index = 0;
}
