#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var userGroup = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);
	userGroup = gfn_getUserGroup();

	ufn_enableBtn("query", false);

	//버튼활성화 처리
	if(userGroup == '20' || userGroup == '30' || userGroup == '40') {
		ufn_enableBtn("query", true);
	}	

	//var curYear = substr(gfn_getCurrDate(),0,4);
	//fn_makeYear(curYear,-3,10);
	//cb_month.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	med_yymm.Value = substr(gfn_getCurrDate(),0,6);
	//코드콤보 설정 : 계약구분
	var codeArr = ["CS135"];
	var dsObjArr = [ds_gnd_cd];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
}

/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum) {
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--) {
		ds_year_cd.AddRow();
		ds_year_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}

	cb_year.index = eNum;
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_gnd.index = 0;
	cb_jpgbn.index = 0;
	ds_gnd_cd.filter("CODE <> 'F'");

	if(G_VEND_CODE == "0000009916") { //서비스사업부
		cb_jpgbn.Value = "1";
		cb_jpgbn.Enable = false;
	} else if(G_VEND_CODE == "0000009935" || G_VEND_CODE == "0000009039") { //주차사업부
		cb_jpgbn.Value = "2";
		cb_jpgbn.Enable = false;
	}

	cb_month.SetFocus();
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	//년필드 필수체크	
	if(gfn_isNull(med_yymm, true, cb_yyyymm.value))
		return;
	//월필드 필수체크	
	//if(gfn_isNull(cb_month, true, st_month.value))
	//	return;
	//제품구분 필수체크	
	if(gfn_isNull(cb_jpgbn, true, st_jpgbn.value))
		return;

	var user_grp = gfn_getUserGroup();

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	//ds_cond.SetColumn(0,"YYMM1",gfn_getBeforeMonth(ds_cond.GetColumn(0,"YEAR")+ds_cond.GetColumn(0,"MONTH"), 1));
	//ds_cond.SetColumn(0,"YYMM2",ds_cond.GetColumn(0,"YEAR")+ds_cond.GetColumn(0,"MONTH"));
	ds_cond.SetColumn(0,"YYMM1",gfn_getBeforeMonth(ds_cond.GetColumn(0,"YYMM2"), 1));
	ds_cond.SetColumn(0,"YYMM2",ds_cond.GetColumn(0,"YYMM2"));
	tit_clearActionInfo();

	if(ed_upn.value == "") {
		ds_cond.SetColumn(0,"UPN","");
		ds_cond.SetColumn(0,"CST","");
	} else {
		ds_cond.SetColumn(0,"UPN",ed_upn.value);

		if(ed_cst.value == "") {
			ds_cond.SetColumn(0,"CST","");
		} else {
			ds_cond.SetColumn(0,"CST",ed_cst.value);
		}
	}

	tit_addSearchActionInfo("cs41:CS4104001A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   :  저장 (변동내역 저장_20120705 LHR)  
********************************************************************************/
function fn_save() {
	// 조회조건 계약내용이 전체로 설정된 경우만 저장 가능
	if(ds_list.GetColumn(0, "CHK") <> "") {				
		gfn_showAlert("계약내용을 '전체'로 조회 한후 저장 버튼을 누르십시오.");
		return;		
	} else {		
		fn_chkSaveCount();		
	}
}

function fn_chkSaveCount() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs41:CS4104001A_S2");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_save_cnt=ds_save_cnt"
		, ""
		, "fn_afterChkSaveCount"
		, true);
}

function fn_afterChkSaveCount() {
	//저장여부 'Y' 인 데이터가 있는 경우 저장 불가능
	if(ds_save_cnt.GetColumn(0, "CNT") > 0) {
		gfn_showAlert("이미 마감되어 저장을 할 수 없습니다.");	
		return false;
	} else {
		fn_save2();
	}
}

function fn_save2() {
	gfn_clearMsgBar();
	ds_save_list.Reset();
	ds_save_list.ClearData();

	for(var i=0; i<ds_list.RowCount(); i++) {
		ds_save_list.AddRow();
		ds_save_list.SetColumn(i, "FLAG", "U");
		ds_save_list.SetColumn(i, "UPN", ds_list.GetColumn(i, "UPN"));
		ds_save_list.SetColumn(i, "CST", ds_list.GetColumn(i, "CST"));
		ds_save_list.SetColumn(i, "GNO", ds_list.GetColumn(i, "GNO"));
		ds_save_list.SetColumn(i, "YMD", ds_list.GetColumn(i, "GYM2"));		
		ds_save_list.SetColumn(i, "LMT", ds_list.GetColumn(i, "LMT"));
		ds_save_list.SetColumn(i, "AMT", ds_list.GetColumn(i, "AMT"));				
		ds_save_list.SetColumn(i, "DMT", ds_list.GetColumn(i, "DMT"));				
		ds_save_list.SetColumn(i, "LOT", ds_list.GetColumn(i, "LOT"));
		ds_save_list.SetColumn(i, "TOT", ds_list.GetColumn(i, "TOT"));
		ds_save_list.SetColumn(i, "DOT", ds_list.GetColumn(i, "DOT"));
		ds_save_list.SetColumn(i, "GND", ds_list.GetColumn(i, "GND"));
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "cs41:CS4104001A_I1"
		, ""
		, "FLAG");

	tit_callService(
		""
		, ""
		, "ds_save_list=ds_save_list"
		, ""
		, ""
		, "fn_afterSave"
		, true);	

}

function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}

	fn_query();
}

