/********************************************************************************
* 기      능   : 업로드 관련 데이타 초기화
********************************************************************************/
function btn_trash_OnClick(obj)
{
	fileDialog.FileName = "" ;
	fileSize = 0 ;
	fileDialog.FilePath = "" ;
	ed_FileName.value = "" ;
}

/********************************************************************************
* 기      능   : 첨부파일 ( 설치 확인증 ) 업로드 데이타 기본 셋팅
********************************************************************************/
function btn_openFile_OnClick(obj)
{
	fileDialog.open();

	if ( length(fileDialog.FilePath) == 0 
		|| length(fileDialog.FileName ) == 0 ) {
		return;
	}

	fileSize = f_tmp.GetLength(fileDialog.FilePath + "\\" + fileDialog.FileName );

	if ( fileSize <= 0 ) {
		gfn_showAlert("CW00048");
		return;
	}

	if ( fileSize > toNumber(F_SIZE)) {
		gfn_showAlert("CW00047", "10 Mbytes" );	
		return;
	}

	ed_FileName.Value = fileDialog.FileName;

}
/********************************************************************************
* 기      능   : 자재청구 문서번호 검색
********************************************************************************/
function ufn_getReqNo(obj)
{
	var reqcond = null;

	reqcond = gfn_openPopup("MM0901003A", true);

    if (reqcond !=null) {
		ed_reqno.value = reqcond[0];
    }
}
