/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	var user_grp = gfn_getUserGroup();

	if(gfn_isNull(cal_startDt, true, st_yymm.value))
		return;


	if(user_grp == '40') {
		if(gfn_isNull(div_code.cb_big, true, st_bsu.value))
			return;
	}

	if(gfn_isNull(div_code.cb_mid, true, st_bsu.value)  && length(trim(ed_upn.Text)) <= 0) {
		var arr = [st_bsu.value];
		gfn_showMsg("CW00001", arr);
		return;
	}
	/*
	if(gfn_isNull(cal_startDt, true)) {
		var arr = ["조회일자"];
		gfn_showMsg("CW00001", arr);
		return;
	}
	*/

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}



//alert(div_code.cb_big.value);	
	//div_code.cb_big.Filter("value=='1' || value=='2'");
	if ( div_code.cb_big.value == '1' || div_code.cb_big.value == '2')
	{
		alert("'지역' or '보수PM' 은 선택하실수 없습니다.");
		return;
	}

	tit_clearActionInfo();	
	tit_addSearchActionInfo("cs41:CS4110001A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , ""
        , true);







//alert("aaaaaa");  

	for(var i=0; i<ds_list.rowcount; i++) 
	{		

		//=================(03)도로명 주소 없을시 지번주소를 도로명 주소로 넘겨줌============================
		if(length(trim(ds_list.GetColumn(i, "KD_ADR"))) <= 0)
		{
			ds_list.SetColumn(i, "KD_ADR", ds_list.GetColumn(i, "KG_ADR"));
        }



        //============================부가세 계산  =======================================
        if(ds_list.GetColumn(i, "CS121_JZR") == 'N' )
        {	/*  
			var a_amt, a_vat, a_tot;
			a_amt = ds_list.GetColumn(i, "AMT");
			a_vat = parseInt((a_amt * 1.1) / 10) * 10;
			a_vat = (a_amt * 0.1) + (a_vat - (a_amt * 1.1));
			a_tot = a_amt + a_vat;
			//a_tot = a_vat;

			ds_list.SetColumn(i, "AMT",  a_tot);
			ds_list.SetColumn(i, "TOT",  a_tot);
			ds_list.SetColumn(i, "TOT2", a_tot);
			ds_list.SetColumn(i, "TOT3", a_tot);
			*/

			var amt, round_vat, org_tot,  round_tot, diff, a_vat, a_tot;
			amt       = ds_list.GetColumn(i, "AMT");
			round_vat = round(amt * 0.1, 0);

			org_tot 	= amt + round_vat;
			round_tot	= round(org_tot / 10 , 0);
			diff        = (round_tot * 10) - org_tot;
			a_vat       = round_vat + diff;
			a_tot       = amt + a_vat;

			ds_list.SetColumn(i, "AMT",  a_tot);
			ds_list.SetColumn(i, "TOT",  a_tot);
			ds_list.SetColumn(i, "TOT2", a_tot);
			ds_list.SetColumn(i, "TOT3", a_tot);			

        }


        //====================지로번호 ========================================
        lv_gro 	  	= '';
		lv_tmpgro 	= '';
		lv_ck_digit = '';


        lv_cupn      = ufn_check_digit('C', ds_list.GetColumn(i, "CS131_GNO")); //변경 통합프로젝트 번호
        ds_list.SetColumn(i, "CUPN", lv_cupn);			   

        lv_tmpgro = ''         + ds_list.GetColumn(i, "CUPN") + ds_list.GetColumn(i, "CS131_YYM") + ds_list.GetColumn(i, "REGIO") + '1' + right(ds_list.GetColumn(i, "VBELN"),4) ;

        lv_ck_digit  = ufn_check_digit('A', lv_tmpgro);                   		//고객코드  디지트        
        lv_ck_digit2 = ufn_check_digit('B', ds_list.GetColumn(i, "TOT")); 		//금액      디지트


        	 //지로번호(고정) / 변경통합프로젝트번호       /  청구년월                          /  지역코드                    /정기보수/  영업문서번호
		lv_gro    = '4518938'  + ds_list.GetColumn(i, "CUPN") + ds_list.GetColumn(i, "CS131_YYM") + ds_list.GetColumn(i, "REGIO") + '1' + right(ds_list.GetColumn(i, "VBELN"),4)  + 		            
		        //디지트12방식  /           계약금액                  /    디지트731   /  세금코드(고정)
		           lv_ck_digit + toString(ds_list.GetColumn(i, "TOT")) +  lv_ck_digit2 + ds_list.GetColumn(i, "TCD")  ;

        ds_list.SetColumn(i, "GRO", lv_gro);  //전체지로번호
        ds_list.SetColumn(i, "GRO1", '<' + '4518938+');  //지로1  
        ds_list.SetColumn(i, "GRO2", '+' + ds_list.GetColumn(i, "CUPN") + ds_list.GetColumn(i, "CS131_YYM") + ds_list.GetColumn(i, "REGIO") + '1' + right(ds_list.GetColumn(i, "VBELN"),4)  + lv_ck_digit + '+');  //지로2  
        ds_list.SetColumn(i, "GRO3", '+' + toString(ds_list.GetColumn(i, "TOT")) +  lv_ck_digit2 + '<' );  //지로3
        ds_list.SetColumn(i, "GRO4", '<' + ds_list.GetColumn(i, "TCD") + '<' );  //지로4 


        //================================전자납부번호========================== ds_list.GetColumn(i, "CUPN")
        lv_dnnum = substr(ds_list.GetColumn(i, "KNUM"), 4, 6) + ds_list.GetColumn(i, "CUPN") + '00' ;         
        ds_list.SetColumn(i, "DNNUM", lv_dnnum);
    }

    ds_list2.copy(ds_list);
}

