function ufn_check_digit(gbn, chkdate)
{
	var chkchar = '';	
	var lv_chk_num  = 0;
	var lv_num  	= 0;  
	var lv_rest 	= 0;
	var lv_weight 	= 0;	
	var lv_chk_digit3 = 0;
	var lv_chk_digit2 = 0;
	var lv_chk_digit  = 0;
	var lv_len = length(trim(chkdate));

	switch(gbn) {
		case "A" :  //고객번호
			for(var i=0; i<lv_len; i++)
			{
				lv_chk_num = toNumber(substr(chkdate, i, 1));
				lv_num = lv_num + 1;
				lv_rest = lv_num % 2;

				if (lv_rest == 0 )  lv_weight = 1; 
				else if (lv_rest == 1 )  lv_weight = 2; 

				lv_chk_digit3 = lv_chk_num * lv_weight;

				if (lv_chk_digit3 > 9)  lv_chk_digit3 = lv_chk_digit3 % 10 + 1;

				lv_chk_digit2 = lv_chk_digit2 + lv_chk_digit3;
			}

			lv_chk_digit = 10 - ( lv_chk_digit2 % 10) ;
			if (lv_chk_digit == 10)  lv_chk_digit = 0;
			chkchar = ToString(lv_chk_digit);
			break;

		case "B" :  //금액
			lv_num = lv_len + 1;
			for(var i=0; i<lv_len; i++)
			{
				lv_chk_num = toNumber(substr(chkdate, i, 1));
				lv_num = lv_num - 1;
				lv_rest = lv_num % 3;

				if (lv_rest == 0 )  lv_weight = 1; 
				else if (lv_rest == 1 )  lv_weight = 7; 
				else if (lv_rest == 2 )  lv_weight = 3; 

				lv_chk_digit2 = lv_chk_digit2 + (lv_chk_num * lv_weight);												
			}

			lv_chk_digit = 10 - ( lv_chk_digit2 % 10) ;
			if (lv_chk_digit == 10)  lv_chk_digit = 0;
			chkchar = ToString(lv_chk_digit);
			break;		

		case "C" :  //변경 통합 프로젝트 번호
			if(substr(chkdate, 0, 1) == '1') {
				chkchar = '8' + substr(chkdate, 1, 5);
			}else if(substr(chkdate, 0, 1) == '2') {
				chkchar = '9' + substr(chkdate, 1, 5);
			}else if(substr(chkdate, 0, 1) == 'M') {
				chkchar = '3' + substr(chkdate, 1, 5);
			}else if(substr(chkdate, 0, 1) == 'N') {
				chkchar = '4' + substr(chkdate, 1, 5);
			}else if(substr(chkdate, 0, 1) == 'J') {
				chkchar = '5' + substr(chkdate, 1, 5);
			}else {
				chkchar = substr(chkdate, 0, 6);
			}

			//chkchar = ToString();
			break;								
	}


	return trim(chkchar);
}


