/********************************************************************************
* 기      능   : 작업 로그 체크 zcst704
********************************************************************************/
function fn_checkLog() {

	ds_cond.SetColumn(0, "LOG", "Z");
	tit_clearActionInfo();																	
	tit_addSearchActionInfo("cs42:CS4201001A_S4");									
	tit_callService(		
			""
			, ""							
			, "ds_cond=ds_cond"							
			, "ds_log=ds_log"							
			, ""							
			, ""							
			, true							
			, false							
			, true); 
}
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	ds_list.ClearData();
	ds_log.ClearData();

	// 로그 체크 
	fn_checkLog();
	if(ds_log.RowCount() >= 1) {	
		gfn_openPopup("CS4201001G",true);
		return false;	
	}


	ds_list.ClearData();
	tit_clearActionInfo();

	tit_addSearchActionInfo("cs42:CS4202001A_S2");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"
		, ""
		, "fn_afterQuery"
		, true
		, ""
		, true
	);
}
/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

