function btn_trans_OnClick(obj)
{

	// if(G_USER_ID <> "2021200") {
		// alert("조직개편 전산 반영 작업으로 인해 8월 17일 오후부터 사용가능합니다. 문의 : 정보기술부 이하림");
		// return;
	// }

	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showAlert("CW00013");		//처리할 자료를 먼저 선택해 주십시오.
		return;
	}

	ds_list_copy.ClearData();
	var j = 0;
	var giro = 0;
	var cms  = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) {

			// 우편번호 체크
			var post_chk  = ds_list.GetColumn(i,"POSTCD1");
			var post_chk2 = ds_list.GetColumn(i,"POSTCD5");
			post_chk  = replace(post_chk , " ", "");
			post_chk  = replace(post_chk , "_", "");
			post_chk  = replace(post_chk , "-", "");
			post_chk  = replace(post_chk , "000000", "");
			post_chk2 = replace(post_chk2 , " ", "");
			post_chk2 = replace(post_chk2 , "_", "");
			post_chk2 = replace(post_chk2 , "-", "");
			post_chk2 = replace(post_chk2 , "00000", "");

			if(length(post_chk2) <> 5) {
				if(length(post_chk) < 6) {
					gfn_showAlert(i + 1  + " 번째 행의 수취인 우편번호 정보를 확인하십시오.");
					return false;	
				}
			}
			// 주소 체크 
			var s079 = ds_list.GetColumn(i,"S079");
			s079 = replace(s079 , "-" ,"");
			if(length(s079) ==0) {
					gfn_showAlert(i + 1  + " 번째 행 거래선의 지로 수신처 주소 정보를 확인하십시오.");
					return false;	

			}
			if(length(ds_list.GetColumn(i, "TAX1004")) > 0) {
					gfn_showAlert(i + 1  + " 번째 행의 계산서 정보를 확인해주세요.");
					return false;	
			}
			if(ds_list.GetColumn(i,"PRIFORM") != "5") {
				if(ds_list.GetColumn(i, "AMT") != ds_list.GetColumn(i, "AMT2")) {
						gfn_showAlert(i + 1  + " 번째 행의 지로 DATA에 오류가 있습니다.  ");
						return false;	
				}
			}

			ds_list_copy.AddRow();
			ds_list_copy.SetColumn(j,"TAXNO"  , ds_list.GetColumn(i,"TAXNO"));
			ds_list_copy.SetColumn(j,"PRIFORM", ds_list.GetColumn(i,"PRIFORM"));
			ds_list_copy.SetColumn(j,"S004", ds_list.GetColumn(i,"S004"));
			ds_list_copy.SetColumn(j,"SEAL", ds_list.GetColumn(i,"SEAL"));
			if(ds_list.GetColumn(i,"PRIFORM") == "5") {
				cms ++;
			} else{
				giro ++;
			}
			j++;
		}	
	}	

	if(!gfn_showConfirm("전자우편 서비스 신청을 하시겠습니까?          " + "지로" +  giro + " 건 / CMS " + cms+ " 건")) return;

	//progressbar
	fn_createWait("", this);

	fn_makeLog();
	btn_trans.Enable = false ;

	tit_callService(
        "CS4201001A_ACT"
       // , "insertHeader"
        , "insertJiroCms"
        , "ds_list=ds_list_copy"
        , "ds_list=ds_list ds_error=ds_error"
        , ""
        , "fn_afterInsert");	


}
function fn_afterInsert(errCode, errMsg) {

	if(errCode != 0) {
		gfn_showAlert("CE00001");
	} else {
		gfn_showAlert("전자우편 서비스 신청이 완료되었습니다.");
		btn_trans.Enable = false ;

		btn_getTaxinfo.Enable = true ;

		fn_query();
	}
}
function btn_getTaxinfo_OnClick(obj)
{	// 기출력의 경우는 프로젝트 필수


	gfn_showAlert("출력대상 조회 ZCSR185에서 처리해주세요. "
	               + "\n" +"문의 : 정보기술부 이하림,판매관리팀 이경해");

	//return;


	if(G_USER_ID != "2021200" && G_USER_ID != "2018900" && G_USER_ID != "2029758" && G_USER_ID != "2999999") {
		btn_trans.Enable = false;
	}

	if(cb_print.Value == "B") {
		if(length(Trim(ds_cond.GetColumn(0,"PJT"))) == 0) {
			gfn_showAlert("기출력으로 조회시 프로젝트가 필수 입니다.");
			return false;
		}
	}
	//부서코드 설정
	var vkbur = "";
	vkbur = ds_vkgrp_cd.GetColumn(ds_vkgrp_cd.FindRow("CODE",ds_cond.GetColumn(0, "VKGRP")),"DTEXT1");
	ds_cond.SetColumn(0, "VKBUR", vkbur);
	trace(ds_cond.SaveXML());

	//progressbar
	fn_createWait("", this);
	ds_list.ClearData();
	//전자우편 신청되지 않은 세금계산서 정보 삭제
	fn_delTaxinfo();

	return;
	tit_callService(
        "CS4201001A_ACT"
        , "getTaxinfo"
        , "ds_cond=ds_cond"
        , "ds_list=ds_list ds_error=ds_error"
        , ""
        , "fn_afterGetTaxinfo");	

}
function fn_afterGetTaxinfo(errCode, errMsg) {
	if(ds_error.rowcount() > 0) {
		gfn_showalert(ds_error.GetColumn(0, "ERRMSG"));
		if(ds_error.GetColumn(0, "ERRMSG") == "성공" ) {
			gfn_showMsg("CI00005");
			//trace(ds_error.SaveXML());
			//btn_getTaxinfo.Enable = false ;
			btn_trans.Enable = true ;
		}

	} else {
		if(errCode != 0) {
			gfn_showMsg("CE00001");
		} else {
			gfn_showMsg("CI00005");
			//btn_getTaxinfo.Enable = false ;
		}
	}	
	fn_query();
}
