// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var vBtn_auth = false;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);	


	st_notice.Value = "※ 출력대상 조회 : SAP 프로그램 ZCSR185 사용  "
	               + "\n" +"  전자우편 신청 : 지로/CMS 나눠서 신청 작업";

	cal_dat1.Value = substr(gfn_getCurrDate(),0,6) + "01";
	cal_dat2.Value = gfn_getLeapLastDay(gfn_getCurrDate()); // 당월 마지막 일자 가져오기

	cb_priform.Index = 0; // 지로 or CMS 
	cb_s007.Index    = 0; // 일반 or 등기

	//================= 버튼 활성화 권한 설정 변경(하드코딩에서 공통함수 사용)
	//G_USER_ID in ("2021200","2018900","2029758","2999999")
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS4202001B");
	ds_rcond.SetColumn(0, "GBN", "01");	
	vBtn_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();
	if (!vBtn_auth)
	{
		var arr = split(G_ORG_USER_INFO, "^", true);

		switch(arr[0]){
			case "2021200" :
			case "2018900" :
			case "2029758" :
			case "2999999" :
				vBtn_auth = true;
				break;
			default :
				break;
		}
 	}
	//==============================================
	//지사 콤보 리스트 가져오기
	var codeArr = ["VKGRP"];
    var dsObjArr = [ds_vkgrp_cd];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	// 서비스 본부 팀코드 가져오기
	//ds_vkgrp_cd.Filter("(CODE ='') OR (CODE >= 'E97' AND CODE <= 'EFP')");
	// Major, Metro 조회되도록 처리. 2020.02.14 Han J.H
	ds_vkgrp_cd.Filter("(CODE ='') OR (left(CODE,1) == 'E') AND (CODE != 'EH1') AND (CODE != 'EJ1')");	
	cb_vkgrp.Index = ds_vkgrp_cd.FindRow("CODE", G_SAP_USER_VGCD);
	//btn_query.enable = false; // 조회 비활성화
	btn_query.enable = true; // 조회 비활성화
	// 전체 조회 가능 
	var user_grp = gfn_getUserGroup();
	// 전체 조회 가능 
	if(user_grp == "40"){
		// Major, Metro 조회되도록 처리. 2020.02.14 Han J.H
		//ds_vkgrp_cd.Filter("(CODE ='') OR (CODE >= 'E97' AND CODE <= 'EFP')");
		cb_vkgrp.Index = 0;
		cb_vkgrp.Enable = true;
		btn_query.enable = true; // 조회 비활성화
	} else {
		// 업체 코드 
		cb_vkgrp_OnChanged();
	}

	// var arr = split(G_ORG_USER_INFO, "^", true);
	// if( arr[0] == "2021200" or G_USER_ID == "2021200" or arr[0] == "2018900" or G_USER_ID == "2018900" or arr[0] == "2029758" or G_USER_ID == "2029758" or arr[0] == "2999999" or G_USER_ID == "2999999"){ 
	if (vBtn_auth)
	{
		btn_trans.Enable   = true;
		btn_query.enable   = true;
		ed_rowcnt.Visible  = true;
 		btn_rowcnt.Visible = true;
 	}
	fn_getPostCodelist(); // ZCST703 코드 리스트 
}

