function fn_checkLog(arr) {

	ds_cond.SetColumn(0, "LOG", arr);
	tit_clearActionInfo();																	
	tit_addSearchActionInfo("cs42:CS4201001A_S4");									
	tit_callService(		
			""
			, ""							
			, "ds_cond=ds_cond"							
			, "ds_log=ds_log"							
			, ""							
			, ""							
			, true							
			, false							
			, true); 
}
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	ds_list.ClearData();
	ds_log.ClearData();

	// 로그 체크 
	fn_checkLog("Z");
	if(ds_log.RowCount() >= 1) {	
		gfn_openPopup("CS4201001G",true);
		return false;	
	}

	tit_clearActionInfo();

	if(cb_priform.Value == "A") { // 지로 
		tit_addSearchActionInfo("cs42:CS4202001B_S2JIRO");
	} else {					  // cms 
		tit_addSearchActionInfo("cs42:CS4202001B_S3CMS");
	} 
	// 요약정보 
	tit_addSearchActionInfo("cs42:CS4202001B_S4");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list ds_summary=ds_summary"
		, ""
		, "fn_afterQuery"
		, true
		, ""
		, true
	);
}
/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

