// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	if( ds_list.RowCount() > 0 ){
		btn_trans.Enable = true;
	} else {
		btn_trans.Enable = false;
	}
	// 요약정보 
	fn_setSummary();
}
/********************************************************************************
* 기      능   : 요약정보 
********************************************************************************/
function fn_setSummary() {

	st_a.Value = ds_summary.GetColumn(0, "TXT");
	med_cnt1.Value = ds_summary.GetColumn(0, "CNT");

	st_b.Value = ds_summary.GetColumn(1, "TXT");
	med_cnt2.Value = ds_summary.GetColumn(1, "CNT");

	med_sum.value= ds_summary.Sum("CNT");
}
